// Riser furniture item - realistic style, medium complexity
// 3D printable riser/platform with structural details

// Overall dimensions
riser_width = 80;
riser_depth = 60;
riser_height = 40;

// Wall thickness for 3D printing
wall_thickness = 2;

// Leg dimensions
leg_width = 6;
leg_height = 35;

// Step parameters
step_count = 3;
step_height = riser_height / step_count;

module leg(x, y) {
    // Main leg post
    translate([x, y, 0])
        cube([leg_width, leg_width, leg_height], center = true);

    // Leg support brace
    translate([x, y, leg_height/4])
        rotate([45, 0, 0])
        cube([leg_width*0.8, wall_thickness, leg_width*1.5], center = true);
}

module step_platform(level) {
    step_z = level * step_height;
    platform_width = riser_width - (level * 8);
    platform_depth = riser_depth - (level * 6);

    // Main step platform
    translate([0, 0, step_z])
        cube([platform_width, platform_depth, wall_thickness], center = true);

    // Front edge lip
    translate([0, platform_depth/2 - wall_thickness/2, step_z + wall_thickness/2])
        cube([platform_width, wall_thickness, wall_thickness*2], center = true);

    // Side edge lips
    translate([platform_width/2 - wall_thickness/2, 0, step_z + wall_thickness/2])
        cube([wall_thickness, platform_depth, wall_thickness*2], center = true);
    translate([-platform_width/2 + wall_thickness/2, 0, step_z + wall_thickness/2])
        cube([wall_thickness, platform_depth, wall_thickness*2], center = true);
}

module support_beam(x1, y1, x2, y2, z) {
    length = sqrt(pow(x2-x1, 2) + pow(y2-y1, 2));
    angle = atan2(y2-y1, x2-x1);

    translate([(x1+x2)/2, (y1+y2)/2, z])
        rotate([0, 0, angle])
        cube([length, wall_thickness, wall_thickness*1.5], center = true);
}

// Main riser structure
difference() {
    // Base structure
    union() {
        // Top platform
        translate([0, 0, riser_height])
            cube([riser_width, riser_depth, wall_thickness], center = true);

        // Steps
        for(i = [1:step_count-1]) {
            step_platform(i);
        }

        // Legs at corners
        leg(riser_width/2 - leg_width/2, riser_depth/2 - leg_width/2);
        leg(-riser_width/2 + leg_width/2, riser_depth/2 - leg_width/2);
        leg(riser_width/2 - leg_width/2, -riser_depth/2 + leg_width/2);
        leg(-riser_width/2 + leg_width/2, -riser_depth/2 + leg_width/2);

        // Mid support legs
        leg(0, riser_depth/2 - leg_width/2);
        leg(0, -riser_depth/2 + leg_width/2);

        // Support beams
        support_beam(-riser_width/2 + leg_width, -riser_depth/2 + leg_width, 
                     riser_width/2 - leg_width, -riser_depth/2 + leg_width, 
                     leg_height/2);
        support_beam(-riser_width/2 + leg_width, riser_depth/2 - leg_width, 
                     riser_width/2 - leg_width, riser_depth/2 - leg_width, 
                     leg_height/2);
    }

    // Hollow out interior for printability
    translate([0, 0, riser_height/2])
        cube([riser_width - wall_thickness*2, 
              riser_depth - wall_thickness*2, 
              riser_height - wall_thickness], center = true);
}

// Non-slip texture dots on top surface
module grip_dot(x, y) {
    translate([x, y, riser_height + wall_thickness])
        cylinder(h=wall_thickness/2, r=1.5, $fn=8);
}

for(x = [-riser_width/2 + 10 : 10 : riser_width/2 - 10]) {
    for(y = [-riser_depth/2 + 8 : 8 : riser_depth/2 - 8]) {
        grip_dot(x, y);
    }
}

// Corner radius details for realistic look
module corner_detail(x, y, z) {
    translate([x, y, z])
        cylinder(h=wall_thickness, r=wall_thickness, $fn=16);
}

// Add corner details to top
for(x = [-riser_width/2, riser_width/2]) {
    for(y = [-riser_depth/2, riser_depth/2]) {
        corner_detail(x, y, riser_height);
    }
}

