// Create tread body
module tread_body() {
    difference() {
        // Main body
        cube([length, width, height], center=true);

        // Hollow interior
        translate([0, 0, -wall/2])
            cube([length-wall*2, width-wall*2, height], center=true);
    }
}

// Create tread pattern grooves
module tread_pattern() {
    groove_width = 3;
    groove_depth = 1.5;
    spacing = 8;
    count = floor(length / spacing) - 1;

    for (i = [-count/2:count/2]) {
        translate([i * spacing, 0, height/2 - groove_depth/2])
            cube([groove_width, width - wall*2, groove_depth], center=true);
    }
}

// Create side reinforcement ribs
module side_ribs() {
    rib_width = 4;
    rib_height = 15;
    spacing = 20;
    count = floor(length / spacing) - 1;

    for (i = [-count/2:count/2]) {
        translate([i * spacing, 0, 0])
            cube([rib_width, width, rib_height], center=true);
    }
}

// Create mounting holes for furniture attachment
module mounting_holes() {
    hole_diameter = 6;
    counter_sink_diameter = 10;
    counter_sink_depth = 2;

    // Define hole positions
    positions = [
        [-length/2 + 15, -width/2 + 10],
        [-length/2 + 15, width/2 - 10],
        [length/2 - 15, -width/2 + 10],
        [length/2 - 15, width/2 - 10]
    ];

    for (pos = positions) {
        // Counter sink
        translate([pos[0], pos[1], height/2 - counter_sink_depth/2])
            cylinder(h=counter_sink_depth, d1=counter_sink_diameter, d2=hole_diameter, center=true);

        // Through hole
        translate([pos[0], pos[1], -height/2])
            cylinder(h=height, d=hole_diameter, center=true);
    }
}

// Create textured grip surface
module grip_surface() {
    dot_diameter = 2;
    dot_height = 0.5;
    spacing = 5;

    for (x = [-length/2 + 10:spacing:length/2 - 10]) {
        for (y = [-width/2 + 5:spacing:width/2 - 5]) {
            translate([x, y, height/2 - dot_height/2])
                cylinder(h=dot_height, d=dot_diameter, center=true);
        }
    }
}

// Create chamfered edges
module chamfer_edges() {
    edge_radius = 2;

    // Top edges
    translate([0, 0, height/2])
        minkowski() {
            cube([length-wall*2, width-wall*2, 0.1], center=true);
            cylinder(h=0.1, r=edge_radius, center=true);
        }

    // Bottom edges
    translate([0, 0, -height/2])
        minkowski() {
            cube([length-wall*2, width-wall*2, 0.1], center=true);
            cylinder(h=0.1, r=edge_radius, center=true);
        }
}

// Main assembly
union() {
    // Basic tread structure
    tread_body();

    // Add tread pattern on top
    tread_pattern();

    // Add side reinforcement
    translate([0, 0, -height/2 + wall])
        side_ribs();

    // Add grip surface
    grip_surface();

    // Add chamfered edges
    chamfer_edges();
}

// Remove mounting holes
difference() {
    children();
    mounting_holes();
}

