// overall scale
scale_factor = 1;

// main beam dimensions
beam_length = 120 * scale_factor;
beam_width = 30 * scale_factor;
beam_height = 20 * scale_factor;
wall_thickness = 2 * scale_factor;

// joint notch
notch_width = 10 * scale_factor;
notch_depth = 6 * scale_factor;

// bolt holes
bolt_diam = 4 * scale_factor;
bolt_head_diam = 7 * scale_factor;
bolt_head_depth = 3 * scale_factor;

// chamfer
chamfer = 2 * scale_factor;

module rafter_beam() {
    difference() {
        // main beam with chamfered edges
        union() {
            translate([beam_length/2, beam_width/2, beam_height/2])
                cube([beam_length, beam_width, beam_height], center = true);
            // chamfers along top edges
            for (x = [beam_length/2 - chamfer, -beam_length/2 + chamfer]) {
                translate([x, beam_width/2, beam_height - chamfer/2])
                    rotate([0, 45, 0])
                    cube([beam_length - 2*chamfer, chamfer*sqrt(2), chamfer*sqrt(2)], center = true);
            }
            // chamfers along bottom edges
            for (x = [beam_length/2 - chamfer, -beam_length/2 + chamfer]) {
                translate([x, beam_width/2, chamfer/2])
                    rotate([0, -45, 0])
                    cube([beam_length - 2*chamfer, chamfer*sqrt(2), chamfer*sqrt(2)], center = true);
            }
        }

        // hollow core
        translate([beam_length/2, beam_width/2, beam_height/2])
            cube([beam_length - 2*wall_thickness,
                  beam_width - 2*wall_thickness,
                  beam_height - 2*wall_thickness], center = true);

        // joint notch left side
        translate([beam_length - notch_depth/2, beam_width/2, beam_height/2])
            cube([notch_depth, notch_width, beam_height], center = true);

        // joint notch right side
        translate([notch_depth/2, beam_width/2, beam_height/2])
            cube([notch_depth, notch_width, beam_height], center = true);

        // bolt holes left
        translate([beam_length - 15, beam_width/2, beam_height/2])
            rotate([90, 0, 0])
            cylinder(h = beam_width, d = bolt_diam, center = true);

        // bolt holes right
        translate([15, beam_width/2, beam_height/2])
            rotate([90, 0, 0])
            cylinder(h = beam_width, d = bolt_diam, center = true);

        // countersinks left
        translate([beam_length - 15, 0, beam_height/2])
            rotate([90, 0, 0])
            cylinder(h = beam_width/2 + 1, d1 = bolt_head_diam, d2 = bolt_diam, center = true);

        translate([beam_length - 15, beam_width, beam_height/2])
            rotate([90, 0, 0])
            cylinder(h = beam_width/2 + 1, d1 = bolt_head_diam, d2 = bolt_diam, center = true);

        // countersinks right
        translate([15, 0, beam_height/2])
            rotate([90, 0, 0])
            cylinder(h = beam_width/2 + 1, d1 = bolt_head_diam, d2 = bolt_diam, center = true);

        translate([15, beam_width, beam_height/2])
            rotate([90, 0, 0])
            cylinder(h = beam_width/2 + 1, d1 = bolt_head_diam, d2 = bolt_diam, center = true);
    }
}

// grain texture bumps on top surface
module grain_texture() {
    for (i = [0:5:beam_length - 15]) {
        for (j = [3:6:beam_width - 6]) {
            translate([i - beam_length/2 + 7.5, j - beam_width/2, beam_height + 0.2])
                scale([1, 1.5, 0.3])
                sphere(d = 1.2 * scale_factor, $fn = 8);
        }
    }
}

union() {
    rafter_beam();
    grain_texture();
}

