    bench_structure();

    // Legs with proper spacing
    for (x = [-bench_width/2 + thickness*6, bench_width/2 - thickness*6]) {
        tapered_leg(x);
    }

    // Support rails for stability
    support_rail();
    translate([0, bench_depth/2 - thickness, bench_height/2])
        cube([bench_width - thickness*6, thickness, bench_height], center=true);

    // Ridge backrest elements
    for (i = [0:ridge_count-1]) {
        x_pos = (i * bench_width) / (ridge_count - 1);
        height = backrest_height - abs(i - (ridge_count-1)/2) * 2;
        ridge_element(x_pos, height);
    }

    // Top stabilizer bar
    translate([0, 0, bench_height + backrest_height - thickness/2])
        cube([bench_width, thickness*2, thickness], center=true);

    // Side supports for backrest
    for (x = [-bench_width/2 + thickness*3, bench_width/2 - thickness*3]) {
        translate([x, 0, bench_height + backrest_height/2])
            cube([thickness*2, thickness, backrest_height], center=true);
    }
}

// Keystone-shaped stool (furniture piece)
// Dimensions: 120mm height, 150mm width, 150mm depth
// Suitable for 3D printing with 2mm wall thickness

// Overall dimensions
stool_height = 120;
seat_width = 150;
seat_depth = 150;

// Wall thickness for 3D printing
wall_thickness = 2;

// Keystone angle calculation
keystone_angle = 30; // degrees

// Leg dimensions
leg_height = stool_height - 15; // account for seat thickness
leg_width = 20;
leg_depth = 20;

// Seat thickness
seat_thickness = 15;

// Support ring dimensions
support_height = 60;
support_thickness = 8;

// Create the keystone stool
module keystone_stool() {
    // Main seat - keystone shaped top
    difference() {
        // Main keystone shape
        union() {
            // Central rectangular part
            cube([seat_width * 0.6, seat_depth, seat_thickness], center=true);

            // Tapered ends (keystone shape)
            translate([seat_width * 0.3, 0, 0])
                rotate([0, 0, keystone_angle])
                    cube([seat_width * 0.4, seat_depth, seat_thickness], center=true);

            translate([-seat_width * 0.3, 0, 0])
                rotate([0, 0, -keystone_angle])
                    cube([seat_width * 0.4, seat_depth, seat_thickness], center=true);
        }

        // Hollow out for 3D printing
        translate([0, 0, -wall_thickness/2])
            cube([seat_width - wall_thickness*2, seat_depth - wall_thickness*2, seat_thickness + wall_thickness], center=true);
    }

    // Four legs
    leg_positions = [
        [seat_width * 0.35, seat_depth * 0.35],
        [-seat_width * 0.35, seat_depth * 0.35],
        [seat_width * 0.35, -seat_depth * 0.35],
        [-seat_width * 0.35, -seat_depth * 0.35]
    ];

    for(pos = leg_positions) {
        translate([pos[0], pos[1], -leg_height/2 - seat_thickness/2]) {
            difference() {
                // Main leg
                cube([leg_width, leg_depth, leg_height], center=true);

                // Hollow leg for 3D printing
                translate([0, 0, -wall_thickness/2])
                    cube([leg_width - wall_thickness*2, leg_depth - wall_thickness*2, leg_height + wall_thickness], center=true);
            }

            // Leg-to-seat joint reinforcement
            translate([pos[0], pos[1], -seat_thickness/2])
                cylinder(h=10, r=leg_width/2, $fn=20);
        }
    }

    // Support ring connecting legs
    support_width = seat_width * 0.7;
    support_depth = seat_depth * 0.7;

    difference() {
        // Outer support ring
        translate([0, 0, -support_height/2 - seat_thickness/2])
            cube([support_width, support_depth, support_thickness], center=true);

        // Inner cutout
        translate([0, 0, -support_height/2 - seat_thickness/2 - 0.1])
            cube([support_width - wall_thickness*2, support_depth - wall_thickness*2, support_thickness + 0.2], center=true);
    }

    // Corner brackets for additional support
    bracket_size = 10;
    bracket_thickness = 3;

    for(pos = leg_positions) {
        // Diagonal support brackets
        translate([pos[0] * 0.8, pos[1] * 0.8, -seat_thickness/2 - support_height/2]) {
            rotate([0, 0, atan2(pos[1], pos[0])])
                cube([bracket_size, bracket_thickness, support_height/2], center=false);
        }
    }

    // Seat surface texture - slight curvature for comfort
    difference() {
        translate([0, 0, seat_thickness/2 + 0.1])
            sphere(d=seat_width * 1.5, $fn=50);

        translate([0, 0, seat_thickness - 1])
            cube([seat_width * 2, seat_depth * 2, seat_thickness * 2], center=true);
    }
}

// Generate the stool
keystone_stool();

