// Realistic pilaster furniture element
// Dimensions in mm, ready for 3D printing

module pilaster(width=20, depth=20, height=150) {
    // Main body - slightly tapered for realistic look
    difference() {
        // Main column body
        translate([0, 0, height/2])
            scale([0.95, 0.95, 1])
                cube([width, depth, height], center=true);

        // Hollow center for 3D printing efficiency
        translate([0, 0, height/2])
            cube([width-4, depth-4, height-2], center=true);
    }

    // Base molding
    translate([0, 0, 2])
    difference() {
        cube([width+6, depth+6, 4], center=true);
        translate([0, 0, 2])
            cube([width+2, depth+2, 4], center=true);
    }

    // Top capital
    translate([0, 0, height-3])
    difference() {
        cube([width+6, depth+6, 6], center=true);
        translate([0, 0, 3])
            cube([width+2, depth+2, 6], center=true);
    }

    // Fluting details
    for(i=[-3:1:3]) {
        if(i != 0) {
            translate([i*2.5, 0, 10])
                rotate([0, 90, 0])
                    cylinder(h=depth-1, r=0.5, $fn=8);
        }
    }

    // Structural tenon at bottom for furniture joints
    translate([0, 0, 0])
        cylinder(h=5, r=4, $fn=6);

    // Mortise at top for furniture joints
    translate([0, 0, height])
        cylinder(h=5, r=4, $fn=6);

    // Decorative inset panels on front
    translate([0, depth/2-1, height/2])
    difference() {
        cube([width-6, 2, height-20], center=true);
        translate([0, 1, 0])
            cube([width-10, 1, height-24], center=true);
    }

    // Side reinforcements
    translate([width/2-1, 0, height/2])
        cube([2, depth-4, height-10], center=true);
    translate([-width/2+1, 0, height/2])
        cube([2, depth-4, height-10], center=true);
}

