// Realistic baluster for furniture (table/chair leg style)
// Overall height: 120 mm, printable in one piece

// Parameters
base_d = 25;            // Bottom diameter (mm)
top_d = 20;             // Top diameter (mm)
neck_d = 15;            // Narrowest diameter at neck (mm)
bulge_d = 28;           // Widest diameter at bulge (mm)
total_h = 120;          // Total height (mm)
wall_th = 2;            // Wall thickness for 3D printing (mm)
segments = 48;          // Smooth curves

// Main module
module baluster() {
    // Base section - solid cylinder with chamfered bottom
    translate([0, 0, 0])
        cylinder(h=15, d1=base_d+4, d2=base_d, $fn=segments);

    // Transition from base to neck
    translate([0, 0, 15])
        cylinder(h=20, d1=base_d, d2=neck_d, $fn=segments);

    // Neck section - narrowest part
    translate([0, 0, 35])
        cylinder(h=25, d=neck_d, $fn=segments);

    // Bulge section - widest part
    translate([0, 0, 60])
        cylinder(h=30, d1=neck_d, d2=bulge_d, $fn=segments);

    // Upper taper to top
    translate([0, 0, 90])
        cylinder(h=25, d1=bulge_d, d2=top_d, $fn=segments);

    // Top section
    translate([0, 0, 115])
        cylinder(h=5, d=top_d, $fn=segments);

    // Decorative rings using difference
    // Ring 1 - near base
    translate([0, 0, 12])
        difference() {
            cylinder(h=3, d=base_d+2, $fn=segments);
            cylinder(h=3.2, d=base_d-2, $fn=segments);
        }

    // Ring 2 - at bulge
    translate([0, 0, 75])
        difference() {
            cylinder(h=3, d=bulge_d+2, $fn=segments);
            cylinder(h=3.2, d=bulge_d-2, $fn=segments);
        }

    // Ring 3 - below top
    translate([0, 0, 110])
        difference() {
            cylinder(h=3, d=top_d+2, $fn=segments);
            cylinder(h=3.2, d=top_d-2, $fn=segments);
        }

    // Structural reinforcement - internal ribs
