// Main module
module jamb() {
    // Left post
    difference() {
        cube([post_width, depth, height]);

        // Rabbet for door
        translate([post_width-wall_thickness, wall_thickness, 0])
            cube([wall_thickness, depth-wall_thickness*2, height]);

        // Hinge recesses (3 hinges)
        for(h = [20, height/2, height-20]) {
            translate([0, depth-wall_thickness, h-5])
                cube([wall_thickness*2, wall_thickness, 10]);
        }
    }

    // Right post
    translate([width-post_width, 0, 0])
        difference() {
            cube([post_width, depth, height]);

            // Rabbet for door
            translate([0, wall_thickness, 0])
                cube([wall_thickness, depth-wall_thickness*2, height]);

            // Strike plate recess
            translate([post_width-wall_thickness, depth-wall_thickness, height/2-10])
                cube([wall_thickness, wall_thickness, 20]);

            // Bolt recess
            translate([post_width-wall_thickness*2, depth-wall_thickness, height/2-2])
                cube([wall_thickness, wall_thickness, 4]);
        }

    // Header
    translate([post_width, 0, height-header_height])
        difference() {
            cube([width-post_width*2, depth, header_height]);

            // Rabbet for door
            translate([0, wall_thickness, 0])
                cube([width-post_width*2, depth-wall_thickness*2, wall_thickness]);

            // Header detail - molding
            for(i = [1:4]) {
                translate([i*(width-post_width*2)/5, 0, header_height-2])
                    cube([(width-post_width*2)/10, depth, 2]);
            }
        }

    // Threshold
    translate([post_width, 0, 0])
        difference() {
            cube([width-post_width*2, depth, wall_thickness]);

            // Beveled edges
            translate([wall_thickness, 0, 0])
                cube([width-post_width*2-wall_thickness*2, depth, wall_thickness]);
        }

    // Side panels for decorative detail
    // Left panel
    translate([wall_thickness, depth/2, wall_thickness])
        cube([post_width-wall_thickness*2, panel_thickness, height-wall_thickness*2-header_height]);

    // Right panel
    translate([width-post_width+wall_thickness, depth/2, wall_thickness])
        cube([post_width-wall_thickness*2, panel_thickness, height-wall_thickness*2-header_height]);

    // Panel details - vertical grooves
    for(offset = [post_width/4, post_width/2, post_width*3/4]) {
        translate([offset, depth/2+panel_thickness/2, wall_thickness])
            cube([1, panel_thickness/2, height-wall_thickness*2-header_height]);
    }

    translate([width-post_width+offset, depth/2+panel_thickness/2, wall_thickness])
        cube([1, panel_thickness/2, height-wall_thickness*2-header_height]);
}

