// Main shutter module
module shutter() {
    // Outer frame
    difference() {
        // Full back plate
        cube([shutter_w, shutter_h, shutter_d], center=true);

        // Hollow out center
        translate([0, 0, -1])
            cube([shutter_w - 2*frame_w, shutter_h - 2*frame_w, shutter_d + 2], center=true);
    }

    // Horizontal louvers
    slat_count = floor((shutter_h - 2*frame_w) / (slat_h + slat_gap));
    for (i = [0 : slat_count - 1]) {
        y = -shutter_h/2 + frame_w + slat_h/2 + i*(slat_h + slat_gap);
        translate([0, y, 0])
            rotate([louver_angle, 0, 0])
                cube([shutter_w - 2*frame_w - 2, slat_w, shutter_d - 2], center=true);
    }

    // Vertical divider (mullion)
    translate([0, 0, 0])
        cube([2, shutter_h - 2*frame_w, shutter_d], center=true);

    // Hinge pintles on left side
    pintle_r = 2;
    pintle_h = 4;
    for (y = [-shutter_h/2 + 15, 0, shutter_h/2 - 15]) {
        translate([-shutter_w/2 - pintle_r/2, y, -shutter_d/2 + pintle_h/2])
            cylinder(h=pintle_h, r=pintle_r, center=false);
    }

    // Handle
    handle_w = 12;
    handle_h = 8;
    handle_d = 3;
    translate([shutter_w/2 - frame_w - handle_w/2, 0, shutter_d/2 - handle_d/2])
        cube([handle_w, handle_h, handle_d], center=true);

    // Small screw caps for realistic detail
    cap_r = 1.5;
    cap_h = 1;
    for (x = [-shutter_w/2 + frame_w + 5, shutter_w/2 - frame_w - 5]) {
        for (y = [-shutter_h/2 + frame_w + 5, shutter_h/2 - frame_w - 5]) {
            translate([x, y, shutter_d/2 - cap_h/2])
                cylinder(h=cap_h, r=cap_r, center=true);
        }
    }
}

