// Frame profile generation
module frame_profile(width, height, depth, thick) {
    difference() {
        // Outer frame
        cube([width, height, depth], center=true);

        // Inner cutout
        translate([0, 0, -1])
            cube([width - 2*thick, height - 2*thick, depth + 2], center=true);
    }
}

// Single pane with beveled edges
module pane(width, height, thick) {
    difference() {
        cube([width, height, thick], center=true);

        // Bevel edges slightly
        for(x = [-1, 1])
        for(y = [-1, 1])
            translate([x * (width/2 - 1), y * (height/2 - 1), 0])
                cylinder(h=thick+1, r=1, $fn=8);
    }
}

// Horizontal muntin bar
module horizontal_muntin(width) {
    cube([width - 2*wall_thickness, muntin_width, muntin_depth], center=true);
}

// Vertical muntin bar
module vertical_muntin(height) {
    cube([muntin_width, height - 2*wall_thickness, muntin_depth], center=true);
}

// Corner joint detail
module corner_joint() {
    difference() {
        // Joint block
        cube([wall_thickness*2, wall_thickness*2, frame_depth], center=true);

        // 45-degree chamfer
        rotate([0, 0, 45])
            cube([wall_thickness*3, wall_thickness*3, frame_depth+1], center=true);
    }
}

// Main transom assembly
module transom_window() {
    union() {
        // Main frame
        frame_profile(frame_width, frame_height, frame_depth, wall_thickness);

        // Corner joints
        translate([(frame_width/2 - wall_thickness), (frame_height/2 - wall_thickness), 0])
            corner_joint();
        translate([-(frame_width/2 - wall_thickness), (frame_height/2 - wall_thickness), 0])
            rotate([0, 0, 90]) corner_joint();
        translate([(frame_width/2 - wall_thickness), -(frame_height/2 - wall_thickness), 0])
            rotate([0, 0, -90]) corner_joint();
        translate([-(frame_width/2 - wall_thickness), -(frame_height/2 - wall_thickness), 0])
            rotate([0, 0, 180]) corner_joint();

        // Glass panes
        translate([0, 0, 1])
            pane(frame_width - 2*wall_thickness - 2, frame_height - 2*wall_thickness - 2, pane_thickness);

        // Muntin bars
        translate([0, 0, 1])
            horizontal_muntin(frame_width - 2*wall_thickness);
        translate([0, 0, 1])
            vertical_muntin(frame_height - 2*wall_thickness);

        // Small decorative circles at intersections
        translate([0, 0, 2.5])
            cylinder(h=1, r=muntin_width*0.8, $fn=16);
    }
}

