// Veneer Coffee Table with Cross-Laminated Core
// Realistic style, medium complexity (â‰ˆ110 lines)
// Designed for 3D printing (2 mm walls, no overhangs)

$fn = 36; // Smooth curves

// ===== MAIN PARAMETERS =====
table_len = 120;
table_wid = 70;
table_hgt = 45;
veneer_th = 1;
core_th   = 2;

// ===== VENEER LAYERS MODULE =====
module veneer_layer(l, w, t, grain_dir = 0) {
    // Grain direction: 0 = along length, 90 = along width
    difference() {
        cube([l, w, t], center = true);
        // Subtle grain texture: shallow grooves every 2 mm
        for (i = [-l/2 : 2 : l/2]) {
            if (grain_dir == 0) {
                translate([i, 0, t/2 - 0.1])
                    cube([0.3, w, 0.2], center = true);
            } else {
                translate([0, i, t/2 - 0.1])
                    cube([l, 0.3, 0.2], center = true);
            }
        }
    }
}

// ===== LEG MODULE =====
module leg() {
    union() {
        // Tapered square leg with rounded edges
        hull() {
            for (x = [-1, 1])
                for (y = [-1, 1])
                    translate([x*5, y*5, 0])
                        cylinder(h = table_hgt - 2*veneer_th, r = 3, center = false);
        }

        // Veneer wrap
        translate([0, 0, -(table_hgt - 2*veneer_th)/2])
            veneer_layer(12, 12, veneer_th);
    }
}

// ===== ASSEMBLY =====
union() {
    // Tabletop: cross-laminated veneer core
    difference() {
        // Core block with rounded top edge
        minkowski() {
            cube([table_len, table_wid, core_th], center = true);
            cylinder(h = 0.1, r = 2);
        }

        // Recessed underside for weight reduction
        translate([0, 0, -core_th/2 + 1])
            cube([table_len - 8, table_wid - 8, 2], center = true);
    }

    // Top veneer layer (long grain)
    translate([0, 0, core_th/2 + veneer_th/2])
        veneer_layer(table_len, table_wid, veneer_th, 0);

    // Bottom veneer layer (cross grain)
    translate([0, 0, -core_th/2 - veneer_th/2])
        veneer_layer(table_len, table_wid, veneer_th, 90);

    // Apron frame for structural support
    for (side = [0, 1]) {
        // Long sides
        translate([0, side ? table_wid/2 - 3 : -table_wid/2 + 3, -4])
            rotate([0, 90, 0])
                veneer_layer(table_len - 20, 6, veneer_th, 0);

        // Short sides
        translate([side ? table_len/2 - 3 : -table_len/2 + 3, 0, -4])
            rotate([90, 0, 0])
                veneer_layer(table_wid - 20, 6, veneer_th, 90);
    }

    // Legs at corners
    for (x = [-1, 1])
        for (y = [-1, 1])
            translate([x*(table_len/2 - 8), y*(table_wid/2 - 8), -table_hgt/2 - veneer_th])
                leg();

    // Edge banding (visible veneer edges)
    difference() {
        // Main edge profile
        translate([0, 0, 0])
            cube([table_len + 1, table_wid + 1, core_th + 2*veneer_th + 1], center = true);

        // Remove center to leave only edges
        translate([0, 0, 0])
            cube([table_len - 1, table_wid - 1, core_th + 2*veneer_th + 3], center = true);
    }
}

