            translate([0, 0, gutter_depth/2 - 5])
                cube([gutter_width - 4, gutter_length - 4, gutter_depth - 4], center=true);
        }

        // Inner cavity
        translate([0, 0, gutter_depth/2])
            cube([gutter_width - 2*wall_thickness, gutter_length - 2*wall_thickness, gutter_depth - wall_thickness], center=true);

        // Top opening
        translate([0, 0, gutter_depth - wall_thickness/2])
            cube([gutter_width - 2*wall_thickness, gutter_length, wall_thickness + 1], center=true);
    }

    // Mounting brackets
    for(i = [-1, 1]) {
        translate([i * (gutter_width/2 + bracket_width/2), 0, 0])
            bracket();
    }

    // Reinforcement ribs
    for(pos = [-30, 0, 30]) {
        translate([0, pos, gutter_depth/2 - 3])
            rib();
    }

    // Connector joints
    connector_joint([-gutter_length/2 - 2, 0, gutter_depth/2 - 5], 0);
    connector_joint([gutter_length/2 + 2, 0, gutter_depth/2 - 5], 180);
}

// Mounting bracket module
module bracket() {
    difference() {
        union() {
            // Main bracket body
            translate([0, 0, bracket_height/2])
                cube([bracket_width, gutter_length/3, bracket_height], center=true);

            // Support gusset
            translate([0, 0, bracket_height/4])
                rotate([0, 90, 0])
                    cylinder(h=bracket_width/2, r1=bracket_height/2, r2=bracket_height/2);
        }

        // Screw holes
        translate([0, -gutter_length/6, bracket_height/2])
            rotate([90, 0, 0])
                cylinder(h=bracket_width + 2, d=screw_hole_diameter, center=true);
        translate([0, gutter_length/6, bracket_height/2])
            rotate([90, 0, 0])
                cylinder(h=bracket_width + 2, d=screw_hole_diameter, center=true);
    }
}

// Reinforcement rib module
module rib() {
    difference() {
        cube([gutter_width - 2*wall_thickness, 2, 6], center=true);
        translate([0, 0, 3])
            cube([gutter_width - 2*wall_thickness - 4, 4, 6], center=true);
    }
}

// Connector joint for modular assembly
module connector_joint(position, rotation) {
    translate(position)
        rotate([0, 0, rotation]) {
            // Male connector
            translate([0, 0, 0])
                cube([4, gutter_width - 4, 6], center=true);

            // Alignment pins
            for(pin = [-gutter_width/4, gutter_width/4]) {
                translate([2, pin, 0])
                    rotate([90, 0, 0])
                        cylinder(h=4, d=3, center=true);
            }
        }
}

