// Shingle-style side table
// Realistic medium-complexity furniture piece
// Overall footprint: 120mm x 120mm x 120mm

$fn = 32;

// Main parameters
table_top_d = 120;
table_top_t = 8;
leg_t = 20;
leg_h = 100;
apron_h = 60;
shingle_t = 2;
wall_thickness = 2;

module shingle_tile(w, l, t, overlap) {
    // Single cedar shingle tile with realistic taper
    difference() {
        union() {
            // Main shingle body
            translate([0, 0, t/2])
                cube([w, l, t], center=true);

            // Tapered edge
            translate([0, l/2 - overlap/2, t/4])
                cube([w, overlap, t/2], center=true);
        }

        // Taper cut
        translate([0, l/2 - overlap/2, 0])
            rotate([0, 15, 0])
                cube([w*1.2, overlap*1.2, t*2], center=true);
    }
}

module table_top() {
    // Circular top with shingle inlay pattern
    difference() {
        // Main solid top
        cylinder(h=table_top_t, d=table_top_d);

        // Recess for shingles (2mm deep)
        translate([0, 0, table_top_t - shingle_t])
            cylinder(h=shingle_t + 0.1, d=table_top_d - 4);
    }

    // Shingle pattern inlay
    for (ring = [30:30:table_top_d/2 - 15]) {
        tiles = max(8, ceil(2*PI*ring/20));
        for (i = [0:tiles-1]) {
            angle = i * 360/tiles;
            translate([ring*cos(angle), ring*sin(angle), table_top_t - shingle_t])
                rotate([0, 0, angle])
                    shingle_tile(18, 25, shingle_t, 8);
        }
    }
}

module leg(x_offset, y_offset) {
    // Tapered square leg with shingle accent
    translate([x_offset, y_offset, leg_h/2]) {
        // Main leg structure
        difference() {
            cube([leg_t, leg_t, leg_h], center=true);

            // Hollow center for material saving
            translate([0, 0, 0])
                cube([leg_t - wall_thickness*2, leg_t - wall_thickness*2, leg_h - 4], center=true);
        }

        // Shingle accent strips
        for (h = [10:20:leg_h - 20]) {
            translate([0, 0, h - leg_h/2])
                shingle_tile(leg_t + 2, 8, shingle_t, 0);
        }
    }
}

module apron() {
    // Connecting apron between legs with shingle detail
    height = apron_h;

    // Front/back aprons
    for (side = [-1, 1]) {
        translate([0, side*(table_top_d/2 - leg_t/2 - 5), height/2]) {
            difference() {
                cube([table_top_d - leg_t*2 - 10, 10, height], center=true);

                // Cutout for visual lightness
                translate([0, 0, -height/4])
                    cube([table_top_d - leg_t*2 - 20, 12, height/2], center=true);
            }

            // Shingle trim
            for (x = [-40:20:40]) {
                translate([x, 0, height/2 - 2])
                    shingle_tile(18, 12, shingle_t, 0);
            }
        }
    }

    // Left/right aprons
    for (side = [-1, 1]) {
        translate([side*(table_top_d/2 - leg_t/2 - 5), 0, height/2]) {
            rotate([0, 0, 90])
                difference() {
                    cube([table_top_d - leg_t*2 - 10, 10, height], center=true);

                    // Matching cutout
                    translate([0, 0, -height/4])
                        cube([table_top_d - leg_t*2 - 20, 12, height/2], center=true);
                }

            // Shingle trim
            for (y = [-40:20:40]) {
                translate([0, y, height/2 - 2])
                    rotate([0, 0, 90])
                        shingle_tile(18, 12, shingle_t, 0);
            }
        }
    }
}

// Assembly
translate([0, 0, leg_h + table_top_t/2])
    table_top();

// Four legs
for (x = [-1, 1])
    for (y = [-1, 1])
        leg(x*(table_top_d/2 - leg_t/2 - 2), y*(table_top_d/2 - leg_t/2 - 2));

// Apron connecting legs
translate([0, 0, leg_h - apron_h/2])
    apron();

