// Thatch module - creates woven pattern
module thatch_straw(x, y, z, angle) {
    translate([x, y, z]) {
        rotate([0, 0, angle]) {
            // Main straw bundle
            difference() {
                cylinder(h = 12, d1 = thatch_thickness, d2 = thatch_thickness * 0.8);
                // Hollow center for printability
                cylinder(h = 13, d1 = thatch_thickness - 2, d2 = thatch_thickness * 0.8 - 2);
            }
            // Tied section at base
            translate([0, 0, 0.5]) {
                color("brown") {
                    cylinder(h = 3, d = thatch_thickness + 1);
                }
            }
        }
    }
}

// Woven thatch seat
module thatch_seat() {
    union() {
        // Base frame
        difference() {
            cube([length, width, thatch_thickness * 2], center = true);
            cube([length - 4, width - 4, thatch_thickness * 2 + 1], center = true);
        }

        // Thatch bundles - horizontal
        for (i = [-length/2 + 5 : 4 : length/2 - 5]) {
            for (j = [-width/2 + 5 : 6 : width/2 - 5]) {
                thatch_straw(i, j, thatch_thickness, 0);
            }
        }

        // Thatch bundles - vertical (crossing)
        for (i = [-length/2 + 7 : 6 : length/2 - 7]) {
            for (j = [-width/2 + 3 : 4 : width/2 - 3]) {
                thatch_straw(i, j, thatch_thickness + 1, 90);
            }
        }
    }
}

// Leg with thatch wrapping
module thatch_leg(x_pos, y_pos) {
    translate([x_pos, y_pos, -height/2 + 5]) {
        union() {
            // Main leg post
            cylinder(h = height - 10, d = leg_diameter);

            // Thatch wrapping around leg
            for (h = [5 : 8 : height - 15]) {
                rotate([0, 0, h * 15]) {
                    translate([leg_diameter/2 + 1, 0, h]) {
                        thatch_straw(0, 0, 0, 45);
                    }
                }
            }

            // Foot detail
            translate([0, 0, -1]) {
                cylinder(h = 3, d = leg_diameter + 4);
            }
        }
    }
}

// Support frame between legs
module support_frame() {
    // Front support
    translate([0, -width/2 + 15, -height/2 + 15]) {
        cube([length - 20, leg_diameter, leg_diameter], center = true);
    }

    // Side supports
    translate([length/2 - 15, 0, -height/2 + 15]) {
        rotate([0, 90, 0]) {
            cube([width - 20, leg_diameter, leg_diameter], center = true);
        }
    }

    translate([-length/2 + 15, 0, -height/2 + 15]) {
        rotate([0, 90, 0]) {
            cube([width - 20, leg_diameter, leg_diameter], center = true);
        }
    }
}

// Main assembly
union() {
    // Thatch seat
    translate([0, 0, height/2 - 5]) {
        thatch_seat();
    }

    // Four legs
    thatch_leg(length/2 - 15, width/2 - 15);
    thatch_leg(-length/2 + 15, width/2 - 15);
    thatch_leg(length/2 - 15, -width/2 + 15);
    thatch_leg(-length/2 + 15, -width/2 + 15);

    // Support frame
    support_frame();

    // Corner reinforcements
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([x * (length/2 - 15), y * (width/2 - 15), height/2 - 15]) {
                rotate([45, 0, 0]) {
                    cube([leg_diameter * 2, leg_diameter * 2, leg_diameter], center = true);
                }
            }
        }
    }
}

