    // Base frame structure
    union() {
        // Left cantilever leg
        translate([-chair_width/2 + frame_thickness, 0, 0])
            rotate([0, 15, 0])
            cylinder(h=chair_height, r=frame_thickness, $fn=20);

        // Right cantilever leg
        translate([chair_width/2 - frame_thickness, 0, 0])
            rotate([0, -15, 0])
            cylinder(h=chair_height, r=frame_thickness, $fn=20);

        // Rear cross support
        translate([0, -chair_depth/2 + 5, 10])
            rotate([90, 0, 0])
            cylinder(h=chair_width - 2*frame_thickness, r=frame_thickness, $fn=20);

        // Front cross support
        translate([0, chair_depth/2 - 5, 10])
            rotate([90, 0, 0])
            cylinder(h=chair_width - 2*frame_thickness, r=frame_thickness, $fn=20);

        // Seat frame
        seat_frame();

        // Backrest frame
        backrest_frame();

        // Membrane seat
        membrane_seat();

        // Membrane backrest
        membrane_back();
    }
}

// Seat frame structure
module seat_frame() {
    // Left side support
    translate([-chair_width/2 + frame_thickness, 0, seat_height])
        rotate([0, 0, -10])
        cube([frame_thickness*2, chair_depth, frame_thickness]);

    // Right side support
    translate([chair_width/2 - frame_thickness, 0, seat_height])
        rotate([0, 0, 10])
        cube([frame_thickness*2, chair_depth, frame_thickness]);

    // Front support bar
    translate([0, chair_depth/2 - frame_thickness, seat_height + 2])
        rotate([0, 0, 0])
        cube([chair_width - frame_thickness*2, frame_thickness*2, frame_thickness]);

    // Rear support bar
    translate([0, -chair_depth/2 + frame_thickness, seat_height + 2])
        rotate([0, 0, 0])
        cube([chair_width - frame_thickness*2, frame_thickness*2, frame_thickness]);
}

// Backrest frame structure
module backrest_frame() {
    // Left upright
    translate([-chair_width/2 + frame_thickness, -chair_depth/2 + 10, seat_height + back_height/2])
        rotate([10, 0, 0])
        cylinder(h=back_height, r=frame_thickness, $fn=20);

    // Right upright
    translate([chair_width/2 - frame_thickness, -chair_depth/2 + 10, seat_height + back_height/2])
        rotate([10, 0, 0])
        cylinder(h=back_height, r=frame_thickness, $fn=20);

    // Top cross bar
    translate([0, -chair_depth/2 + 10, seat_height + back_height - 5])
        rotate([90, 0, 0])
        cylinder(h=chair_width - 2*frame_thickness, r=frame_thickness, $fn=20);
}

// Membrane seat with realistic stretching effect
module membrane_seat() {
    // Create stretched membrane surface
    difference() {
        // Base membrane
        translate([0, 0, seat_height + membrane_thickness/2])
            scale([1, 1, 0.3])
            sphere(r=chair_width/2, $fn=100);

        // Cut to seat shape
        translate([0, 0, seat_height - 5])
            cube([chair_width + 10, chair_depth + 10, 10], center=true);
    }

    // Add subtle texture pattern for membrane
    for (i = [-3:3]) {
        for (j = [-2:2]) {
            translate([i*8, j*8, seat_height + membrane_thickness + 0.5])
                rotate([90, 0, 0])
                cylinder(h=0.5, r=1, $fn=12);
        }
    }
}

// Membrane backrest
module membrane_back() {
    // Create curved backrest membrane
    difference() {
        // Base curved surface
        translate([0, -chair_depth/2 + 10, seat_height + back_height/2])
            rotate([10, 0, 0])
            scale([1, 0.5, 1])
            sphere(r=chair_width/2, $fn=80);

        // Trim to backrest shape
        translate([0, -chair_depth - 5, seat_height + back_height/2])
            cube([chair_width + 10, chair_depth, back_height + 10], center=true);
    }

    // Reinforcement ribs
    for (i = [-1:1]) {
        translate([i*15, -chair_depth/2 + 10, seat_height + back_height/2])
            rotate([10, 0, 0])
            cube([3, 2, back_height - 10]);
    }
}

