        // Outer tube
        cylinder(h = tube_l, d = tube_d);

        // Hollow center
        translate([0, 0, -1])
            cylinder(h = tube_l + 2, d = tube_d - 2*wall_t);

        // Top taper
        translate([0, 0, tube_l - 20])
            cylinder(h = 25, d1 = tube_d, d2 = nozzle_d);
    }

    // Nozzle
    translate([0, 0, tube_l - 20])
        cylinder(h = nozzle_l, d = nozzle_d);

