        difference() {
            cylinder(h = tube_l/2, d = tube_d + 0.5);
            translate([0, 0, -1])
                cylinder(h = tube_l/2 + 2, d = tube_d - 1);
        }
}

module wall_mount() {
    // Main mounting plate
    difference() {
        cube([wall_mount_w, wall_mount_d, wall_mount_h]);

        // Screw holes
        for (x = [-wall_mount_w/3, wall_mount_w/3]) {
            translate([x, -1, wall_mount_h/2])
                cylinder(h = wall_mount_d + 2, d = 6);
        }
    }

    // Tube cradle
