                cylinder(h = tube_d + 8, d = tube_d + 8);
                cylinder(h = tube_d + 8, d = tube_d);
                translate([-tube_d - 4, 0, 0])
                    cube([tube_d + 8, tube_d + 8, tube_d + 8]);
            }

    // Support ribs
    for (x = [-wall_mount_w/4, wall_mount_w/4]) {
        translate([x, wall_mount_d/2, wall_mount_h/2])
