total_depth = 60;
total_height = 80;

// Wall thickness for 3D printing
wall_thickness = 2;

// Grout texture pattern module
module grout_texture(width, depth, height) {
    for (i = [0:3:width-3]) {
        for (j = [0:3:depth-3]) {
            translate([i, j, height - 1]) {
                cube([2, 2, 1], center = true);
            }
        }
    }
}

// Main body - grout block structure
module grout_body() {
    difference() {
        // Outer shell
        cube([total_width, total_depth, total_height]);

        // Hollow interior
        translate([wall_thickness, wall_thickness, wall_thickness])
            cube([total_width - 2*wall_thickness, 
                  total_depth - 2*wall_thickness, 
                  total_height - wall_thickness]);
    }

    // Add texture on top surface
    grout_texture(total_width - 4, total_depth - 4, total_height);
}

// Support frame underneath
module support_frame() {
    // Front cross beam
    translate([wall_thickness, wall_thickness, -5])
        cube([total_width - 2*wall_thickness, wall_thickness, 5]);

    // Back cross beam
    translate([wall_thickness, total_depth - 2*wall_thickness, -5])
        cube([total_width - 2*wall_thickness, wall_thickness, 5]);

    // Side beams
    translate([wall_thickness, wall_thickness, -5])
        cube([wall_thickness, total_depth - 2*wall_thickness, 5]);

    translate([total_width - 2*wall_thickness, wall_thickness, -5])
        cube([wall_thickness, total_depth - 2*wall_thickness, 5]);
}

// Leg structure with grout texture
module grout_leg(x, y) {
    translate([x, y, -15]) {
        difference() {
            // Main leg
            cube([8, 8, 15]);

            // Hollow inside for material saving
            translate([2, 2, 2])
                cube([4, 4, 11]);
        }

        // Add grout texture
        for (i = [1:2:7]) {
            translate([i, 4, 7.5])
                rotate([90, 0, 0])
                    cylinder(h = 8, r = 0.5, $fn = 8);
        }
    }
}

// Reinforcement ribs inside
module internal_ribs() {
    // Longitudinal ribs
    translate([total_width/2 - 1, wall_thickness, wall_thickness])
        cube([2, total_depth - 2*wall_thickness, total_height - 2*wall_thickness]);

    // Transverse ribs
    translate([wall_thickness, total_depth/2 - 1, wall_thickness])
        cube([total_width - 2*wall_thickness, 2, total_height - 2*wall_thickness]);
}

// Joint connectors
module joint_connectors() {
    // Corner joints
    for (i = [0, 1]) {
        for (j = [0, 1]) {
            translate([i * total_width + (i == 0 ? 5 : -5), 
                      j * total_depth + (j == 0 ? 5 : -5), 
                      total_height/2]) {
                rotate([0, 0, 45])
                    cube([3, 3, 6], center = true);
            }
        }
    }
}

// Main assembly
union() {
    // Main grout body
    grout_body();

    // Support frame
    support_frame();

    // Four legs
    grout_leg(5, 5);
    grout_leg(total_width - 13, 5);
    grout_leg(5, total_depth - 13);
    grout_leg(total_width - 13, total_depth - 13);

    // Internal reinforcement
    %internal_ribs();

    // Joint connectors
    joint_connectors();
}

