wall = 2;                   // uniform wall thickness everywhere

// Helper: hollow cylinder
module hollow_cylinder(od, id, h) {
    difference() {
        cylinder(d = od, h = h, $fn = 48);
        translate([0, 0, -0.1])
            cylinder(d = id, h = h + 0.2, $fn = 48);
    }
}

// Seat: round, slightly domed top surface
module seat() {
    difference() {
        // main seat disc plus gentle dome
        union() {
            cylinder(d = seat_d, h = seat_h, $fn = 48);
            translate([0, 0, seat_h])
                scale([1, 1, 0.2])
                    sphere(d = seat_d, $fn = 48);
        }
        // hollow it out from below
        translate([0, 0, wall])
            cylinder(d = seat_d - 2*wall, h = seat_h + 1, $fn = 48);
    }
}

// Single leg with bottom plug for clean printing
module leg() {
    difference() {
        cylinder(d = leg_d, h = leg_h, $fn = 32);
        // hollow tube
        translate([0, 0, wall])
            cylinder(d = leg_d - 2*wall, h = leg_h - wall, $fn = 32);
    }
    // add small chamfer at bottom for better print finish
    translate([0, 0, -0.5])
        cylinder(d1 = leg_d, d2 = leg_d - 2, h = 2, $fn = 32);
}

// Cross-brace ring between legs
module brace_ring() {
    difference() {
        hollow_cylinder(od = seat_d - 2*leg_d, id = seat_d - 2*leg_d - 2*brace_w, h = brace_w);
    }
}

// Position four legs evenly around seat
module legs_assembly() {
    for (a = [0, 90, 180, 270]) {
        rotate([0, 0, a])
            translate([(seat_d - leg_d)/2, 0, 0])
                rotate([90, 0, 0])
                    leg();
    }
}

// Position brace ring at mid-height
module braces_assembly() {
    translate([0, 0, leg_h/2 - brace_w/2])
        brace_ring();
}

// Final stool
union() {
    // seat on top
    translate([0, 0, leg_h])
        seat();

    // four legs
    legs_assembly();

    // cross-brace ring
    braces_assembly();
}

