module rounded_cube(size, radius) {
    // Creates a cube with rounded edges
    translate([radius, radius, 0])
    minkowski() {
        cube([size.x - 2*radius, size.y - 2*radius, size.z]);
        cylinder(h=0.01, r=radius, $fn=20);
    }
}

module leg(x, y, z) {
    // Chair leg with slightly tapered profile
    translate([x, y, 0])
    linear_extrude(height=z, scale=[0.9, 0.9])
    offset(r=2)
    square([8, 8], center=true);
}

module back_support(x, y, z, angle) {
    // Curved back support slat
    translate([x, y, z])
    rotate([angle, 0, 0])
    difference() {
        rounded_cube([70, 6, 8], 2);
        translate([5, -1, 2])
        cube([60, 8, 4]);
    }
}

module seat_surface() {
    // Contoured seat with ventilation holes
    difference() {
        // Main seat base
        translate([0, 0, 45])
        rounded_cube([80, 70, 6], 4);

        // Ventilation holes pattern
        for (x = [-30, -10, 10, 30]) {
            for (y = [-25, -10, 5, 20]) {
                translate([x, y, 48])
                cylinder(h=10, r=3, $fn=16);
            }
        }

        // Undercut for weight reduction
        translate([0, 0, 45])
        scale([0.8, 0.8, 1])
        rounded_cube([80, 70, 8], 4);
    }
}

module backrest() {
    // Chair backrest with ergonomic curve
    difference() {
        union() {
            // Main back frame
            translate([0, -35, 75])
            rounded_cube([76, 8, 60], 3);

            // Horizontal back slats
            for (i = [1:3]) {
                back_support(0, -31, 50 + i*15, 5);
            }
        }

        // Weight reduction cutouts
        for (i = [1:3]) {
            translate([0, -40, 50 + i*15])
            rotate([5, 0, 0])
            cube([72, 10, 4]);
        }
    }
}

module stretcher(x1, y1, x2, y2, z) {
    // Horizontal support between legs
    translate([(x1+x2)/2, (y1+y2)/2, z])
    rotate([0, 0, atan2(y2-y1, x2-x1)])
    rounded_cube([sqrt(pow(x2-x1,2)+pow(y2-y1,2)), 6, 6], 2);
}

