                    for(z=[2:8:height-2]) {
                        translate([x, y, z])
                            sphere(d=1.2, $fn=12);
                    }
                }
            }
        }
    }

    // Paper backing on front and back
    translate([-wall_th/2, -wall_th/2, -wall_th/2])
        cube([wall_th, depth+wall_th, height+wall_th]);
    translate([width-wall_th/2, -wall_th/2, -wall_th/2])
        cube([wall_th, depth+wall_th, height+wall_th]);
}

// Shelf support bracket
