    // Triangular brace
    union() {
        translate([wall_th*2, 0, 0])
            cube([wall_th, 15, 20]);
        translate([wall_th*2, 0, 0])
            rotate([0, 45, 0])
                cube([wall_th, 15, 14.14]);
