// Insulation Brick Furniture Module
// A stackable insulation brick for modular furniture construction

module insulation_brick(width=40, height=30, depth=60) {
    wall = 2;  // Wall thickness for 3D printing

    // Main foam block with rounded edges
    difference() {
        // Outer rounded block
        minkowski() {
            cube([width-2*wall, height-2*wall, depth-2*wall], center=true);
            sphere(wall);
        }

        // Hollow center
        cube([width-4*wall, height-4*wall, depth-4*wall], center=true);
    }

    // Structural ridges on top for stacking
    for(i = [-1, 1]) {
        translate([i*width/4, 0, depth/2]) {
            cube([width/6, height-wall, wall], center=true);
        }
    }

    // Matching grooves on bottom
    for(i = [-1, 1]) {
        translate([i*width/4, 0, -depth/2]) {
            cube([width/6 + 0.5, height-wall + 0.5, wall], center=true);
        }
    }

    // Side ventilation holes
    for(x = [-width/3, 0, width/3]) {
        for(y = [-height/3, 0, height/3]) {
            translate([x, y, 0]) {
                rotate([0, 90, 0]) {
                    cylinder(h=wall*2, r=wall, center=true, $fn=8);
                }
            }
        }
    }

    // Corner reinforcements
    module corner_reinforcement() {
        difference() {
            cylinder(h=wall*3, r=wall*1.5, center=false, $fn=6);
            cylinder(h=wall*3, r=wall*0.5, center=false, $fn=6);
        }
    }

    for(x = [-1, 1]) {
        for(y = [-1, 1]) {
            translate([x*(width/2 - wall*2), y*(height/2 - wall*2), depth/2 - wall*1.5]) {
                corner_reinforcement();
            }
            translate([x*(width/2 - wall*2), y*(height/2 - wall*2), -depth/2 + wall*1.5]) {
                rotate([180, 0, 0]) corner_reinforcement();
            }
        }
    }

    // Surface texture pattern
    module surface_texture() {
        for(i = [-2:2]) {
            for(j = [-1:1]) {
                translate([i*width/8, j*height/6, depth/2 + 0.1]) {
                    cube([0.5, 0.5, 0.2], center=true);
                }
            }
        }
    }

    // Apply texture to both large faces
    surface_texture();
    mirror([0, 0, 1]) surface_texture();
}

// Create a stack of 3 bricks to demonstrate modularity
translate([0, 0, 0]) insulation_brick();
translate([0, 0, 60]) insulation_brick();
translate([0, 0, 120]) insulation_brick();

