// OpenSCAD code for a realistic medium-complexity wooden sheathing furniture piece
// A small side table with a drawer and sheathing panel sides

wall_th = 2;      // 2 mm shell thickness for 3D printing
scale_factor = 3; // Scale to ~120 mm height

// Overall dimensions (mm)
table_h = 120 * scale_factor;
table_w = 80  * scale_factor;
table_d = 60  * scale_factor;

// Leg dimensions
leg_h = table_h - wall_th;
leg_w = 6;
leg_d = 6;

// Drawer dimensions
drawer_h = 30 * scale_factor;
drawer_w = table_w - 3*wall_th;
drawer_d = table_d - 2*wall_th;

// Sheathing panel dimensions
panel_h = table_h * 0.8;
panel_w = wall_th;
panel_d = table_d;

module leg() {
    difference() {
        cube([leg_w, leg_d, leg_h], center=false);
        // Chamfer bottom edges for printability
        translate([wall_th, wall_th, -1])
            cube([leg_w-2*wall_th, leg_d-2*wall_th, wall_th+1]);
    }
}

module tabletop() {
    difference() {
        cube([table_w, table_d, wall_th], center=false);
        // Rounded top edges
        translate([wall_th, wall_th, 0])
            cube([table_w-2*wall_th, table_d-2*wall_th, wall_th]);
    }
}

module drawer_box() {
    difference() {
        // Main drawer body
        translate([wall_th, wall_th, wall_th])
            cube([drawer_w, drawer_d, drawer_h]);
        // Inner cavity
        translate([2*wall_th, 2*wall_th, 2*wall_th])
            cube([drawer_w-2*wall_th, drawer_d-2*wall_th, drawer_h-3*wall_th]);
    }
}

module drawer_front() {
    difference() {
        // Front panel
        cube([drawer_w+1, wall_th, drawer_h+1]);
        // Recessed handle
        translate([drawer_w/2-5, -0.5, drawer_h/2-3])
            cube([10, wall_th+1, 6]);
    }
}

module sheathing_panel() {
    difference() {
        // Main panel
        cube([panel_w, panel_d, panel_h]);
        // Decorative cutouts
        for (y = [10:20:panel_d-10]) {
            translate([wall_th, y, 10])
                cylinder(h=panel_w, r=3, $fn=16);
            translate([wall_th, y, panel_h-10])
                cylinder(h=panel_w, r=3, $fn=16);
        }
    }
}

// Assemble the table
union() {
    // Tabletop
    translate([0, 0, leg_h])
        tabletop();

    // Four legs
    translate([wall_th, wall_th, 0])
        leg();
    translate([table_w-leg_w-wall_th, wall_th, 0])
        leg();
    translate([wall_th, table_d-leg_d-wall_th, 0])
        leg();
    translate([table_w-leg_w-wall_th, table_d-leg_d-wall_th, 0])
        leg();

    // Drawer cavity support
    translate([wall_th, wall_th, leg_h-drawer_h-2*wall_th]) {
        difference() {
            cube([table_w-2*wall_th, table_d-2*wall_th, drawer_h+2*wall_th]);
            // Drawer opening
            translate([wall_th, wall_th, wall_th])
                cube([drawer_w, drawer_d, drawer_h]);
        }
    }

    // Drawer
    translate([wall_th, wall_th, leg_h-drawer_h-wall_th]) {
        drawer_box();
        translate([0, drawer_d, 0])
            drawer_front();
    }

    // Sheathing panels on sides
    translate([-wall_th, 0, 0])
        sheathing_panel();
    translate([table_w, 0, 0])
        mirror([1,0,0])
            sheathing_panel();
}

// Subfloor Furniture Item - Realistic Style with Medium Complexity
// A raised subfloor panel with joists and support structure

// Overall dimensions
panel_width = 80;
panel_length = 120;
panel_thickness = 12;
joist_height = 25;
joist_width = 18;
leg_height = 30;
leg_diameter = 6;

// Wall thickness for 3D printing
wall_thickness = 2;

// Main subfloor panel with wood grain texture
module subfloor_panel() {
    difference() {
        // Main panel body
        cube([panel_width, panel_length, panel_thickness]);

        // Add wood grain grooves on top surface
        for (i = [5:10:panel_length-10]) {
            translate([wall_thickness, i, panel_thickness-1])
                cube([panel_width-wall_thickness*2, 1, 1]);
        }

        // Chamfer edges
        translate([-1, -1, -1])
            cube([panel_width+2, panel_length+2, 1]);
        translate([-1, -1, panel_thickness])
            cube([panel_width+2, panel_length+2, 1]);
    }
}

// Joist support beams
module joist_beam(length) {
    difference() {
        // Main joist
        cube([joist_width, length, joist_height]);

        // Lightening holes
        for (i = [joist_width*2:joist_width*2:length-joist_width*2]) {
            translate([joist_width/2, i, joist_height/2])
                rotate([90, 0, 0])
                    cylinder(h=joist_width, r=joist_width/3, $fn=6);
        }
    }
}

// Support legs with threaded inserts
module support_leg() {
    union() {
        // Main leg
        cylinder(h=leg_height, r=leg_diameter/2, $fn=8);

        // Foot pad
        translate([0, 0, leg_height])
            cylinder(h=2, r=leg_diameter/2+1, $fn=8);

        // Threaded insert hole
        translate([0, 0, -1])
            cylinder(h=leg_height+2, r=leg_diameter/4, $fn=8);
    }
}

// Corner bracket for reinforcement
module corner_bracket() {
    difference() {
        union() {
            // Main bracket body
            cube([joist_width, joist_width, joist_height]);

            // Diagonal support
            translate([0, 0, 0])
                rotate([0, 0, 45])
                    cube([joist_width*1.4, wall_thickness*2, joist_height]);
        }

        // Screw holes
        translate([joist_width/2, joist_width/2, joist_height/2])
            rotate([90, 0, 0])
                cylinder(h=joist_width, r=1.5, $fn=8);
    }
}

// Assembly
module subfloor_assembly() {
    union() {
        // Main panel
        translate([0, 0, joist_height+leg_height])
            subfloor_panel();

        // Joists
        translate([wall_thickness, wall_thickness, leg_height])
            joist_beam(panel_length-wall_thickness*2);
        translate([panel_width-joist_width-wall_thickness, wall_thickness, leg_height])
            joist_beam(panel_length-wall_thickness*2);
        translate([panel_width/2-joist_width/2, wall_thickness, leg_height])
            joist_beam(panel_length-wall_thickness*2);

        // Support legs
        for (x = [wall_thickness+leg_diameter, panel_width-wall_thickness-leg_diameter]) {
            for (y = [wall_thickness+leg_diameter, panel_length-wall_thickness-leg_diameter]) {
                translate([x, y, 0])
                    support_leg();
            }
        }

        // Corner brackets
        translate([wall_thickness, wall_thickness, leg_height])
            corner_bracket();
        translate([panel_width-joist_width-wall_thickness, wall_thickness, leg_height])
            rotate([0, 0, 90])
                corner_bracket();
        translate([wall_thickness, panel_length-wall_thickness-joist_width, leg_height])
            rotate([0, 0, -90])
                corner_bracket();
        translate([panel_width-joist_width-wall_thickness, panel_length-wall_thickness-joist_width, leg_height])
            rotate([0, 0, 180])
                corner_bracket();
    }
}

// Render the assembly
subfloor_assembly();

