// Gravel Chair - Realistic Style
// Medium complexity 3D printable gravel furniture piece

// Overall dimensions
chair_width = 80;
chair_depth = 90;
chair_height = 85;

// Wall thickness for 3D printing
wall_thickness = 2;

module gravel_shape(base_size, height_variation, detail) {
    // Create irregular gravel-like shape using sphere deformations
    union() {
        // Base ellipsoid
        scale([1 + 0.3 * sin(detail * 5), 
               1 + 0.2 * cos(detail * 7), 
               1 + height_variation])
            sphere(base_size);

        // Add surface bumps
        for(i = [0:8]) {
            rotate([rand(i*3)*30, rand(i*5)*40, rand(i*7)*50])
                translate([rand(i*11)*base_size*0.6 - base_size*0.3,
                          rand(i*13)*base_size*0.6 - base_size*0.3,
                          rand(i*17)*base_size*0.4])
                    sphere(base_size * 0.15);
        }
    }
}

module seat_surface(width, depth, thickness) {
    // Create gravel-textured seat surface
    difference() {
        // Base seat
        translate([-width/2, -depth/2, 0])
            cube([width, depth, thickness]);

        // Create gravel texture pattern
        for(x = [-width/2+5:10:width/2-5]) {
            for(y = [-depth/2+5:10:depth/2-5]) {
                translate([x, y, thickness-0.5])
                    gravel_shape(4, 0.3, x+y);
            }
        }
    }
}

module backrest(width, height, thickness) {
    // Create angled backrest with gravel texture
    translate([0, -chair_depth/2 + 10, chair_height - height]) {
        rotate([10, 0, 0]) {
            difference() {
                // Main backrest panel
                translate([-width/2, 0, 0])
                    cube([width, thickness*2, height]);

                // Add gravel texture
                for(x = [-width/2+5:10:width/2-5]) {
                    for(z = [5:10:height-5]) {
                        translate([x, thickness*2 - 0.5, z])
                            gravel_shape(3.5, 0.2, x+z);
                    }
                }
            }
        }
    }
}

module leg_support(x_pos, y_pos, height) {
    // Create tapered leg with gravel texture
    translate([x_pos, y_pos, 0]) {
        difference() {
