// Aggregate bookshelf (stacked stone / brick look)
// Dimensions: 100mm W Ã— 45mm D Ã— 120mm H
// Wall thickness: 2mm throughout

// Basic parameters
width = 100;
depth = 45;
height = 120;
wall = 2;

// Stone block dimensions
block_w = 12;
block_d = depth * 0.9;
block_h = 6;
gap = 1.5;

// Calculate how many blocks fit
blocks_x = floor(width / (block_w + gap));
blocks_z = floor(height / (block_h + gap));

// Main structure â€“ hollow box
module carcass() {
    difference() {
        cube([width, depth, height]);
        translate([wall, wall, wall])
            cube([width - 2*wall, depth - wall, height - wall]);
    }
}

// Single stone block with rounded edges (printable)
module stone_block(x, y, z) {
    translate([x, y, z]) {
        difference() {
            // Full block
            cube([block_w, block_d, block_h]);

            // Rounded top edges
            translate([block_w/2, block_d/2, block_h - 1])
                cylinder(h = 2, r1 = block_w/2, r2 = block_w/2, $fn = 16);

            // Chamfer bottom edges
            translate([block_w/2, block_d/2, 1])
                cylinder(h = 2, r1 = block_w/2 - 1, r2 = block_w/2, $fn = 16);
        }

        // Add texture lines
        for (i = [0:3]) {
            translate([i * (block_w/4), 0, block_h/2])
                cube([0.5, block_d, block_h - 1]);
        }
    }
}

// Shelf supports
module shelf_support(y_pos) {
    translate([wall, y_pos, wall]) {
        cube([width - 2*wall, wall, height - 2*wall]);
    }
}

// Create stone aggregate pattern
module stone_aggregate() {
    // Bottom row
    for (i = [0:blocks_x - 1]) {
        for (j = [0:blocks_z - 1]) {
            // Staggered pattern
            offset_x = (j % 2 == 0) ? 0 : (block_w + gap)/2;
            x = i * (block_w + gap) + offset_x;
            z = j * (block_h + gap);

            // Only place if within bounds
            if (x + block_w < width) {
                stone_block(x, 0, z);
            }
        }
    }
}

// Back panel with holes for ventilation
module back_panel() {
    translate([0, depth - wall, 0]) {
        difference() {
            cube([width, wall, height]);

            // Ventilation holes
            for (i = [10:20:width - 10]) {
                for (j = [10:20:height - 10]) {
                    translate([i, -0.5, j])
                        cylinder(h = wall + 1, r = 3, $fn = 16);
                }
            }
        }
    }
}

// Assembly
union() {
    carcass();
    back_panel();

    // Three shelves inside
    shelf_support(30);
    shelf_support(60);
    shelf_support(90);

    // Stone aggregate exterior
    stone_aggregate();
}

