// Tie-shaped furniture shelf unit â€“ OpenSCAD
// Overall footprint 120 Ã— 70 Ã— 220 mm â€“ printable on most FDM machines
// Wall thickness 2 mm, no overhangs >45Â°

// --------------- PARAMETERS ---------------
$fn = 36;                  // Smooth curves
wall = 2;                  // Uniform wall thickness
tieW = 70;                 // Widest point of the tie knot
tieL = 120;                // Total length of the tie body
bodyH = 220;               // Furniture height (tie hangs vertically)
shelfT = 8;                // Shelf board thickness
gap = 35;                  // Vertical gap between shelves
pinD = 4;                  // Dowel pin diameter
pinH = 8;                  // Dowel pin length

// --------------- MODULES ---------------
// Simple 2 mm shell for any 2D contour
module shell_2mm(profile, h) {
    linear_extrude(height = h, center = false)
        offset(delta = -wall)
            profile;
}

// Shelf board with rounded front edge
module shelf(w, d) {
    difference() {
        // Main shelf
        translate([0, 0, 0])
            shell_2mm(
                offset(delta = wall)
                    square([w, d], center = true),
                shelfT
            );
        // Front round-over (no overhang)
        translate([0, d/2 - wall/2, shelfT - wall])
            rotate([90, 0, 0])
                cylinder(h = d, r = wall, center = true);
    }
}

// Dowel pins for shelf support
module pin() {
    cylinder(h = pinH, d = pinD);
}

// --------------- MAIN TIE SHAPE ---------------
// Knot â€“ truncated cone
module knot() {
    difference() {
        cylinder(h = 30, d1 = tieW - 20, d2 = tieW, center = true);
        // Hollow core
        translate([0, 0, -1])
            cylinder(h = 32, d1 = tieW - 20 - 2*wall, d2 = tieW - 2*wall, center = true);
    }
}

// Body â€“ tapering prism with rounded back
module body() {
    hull() {
        // Front face
        translate([0, 0, 0])
            cube([tieL, wall, bodyH], center = true);
        // Back face
        translate([0, 0, 0])
            cube([tieL - 40, wall, bodyH - 100], center = true);
    }
}

// --------------- SHELVES ---------------
// Vertical positions inside the tie body
shelf_offsets = [50, 50 + gap, 50 + 2*gap];

// Generate shelves and hidden support pins
module shelves() {
    for (z = shelf_offsets) {
        // Left shelf
        translate([-tieL/4, 0, z])
            shelf(tieW/2 - 10, 60);
        // Right shelf
        translate([tieL/4, 0, z])
            shelf(tieW/2 - 10, 60);

        // Hidden dowels (inside body)
        translate([-tieL/4 - 5, 0, z - pinH/2])
            pin();
        translate([-tieL/4 + 5, 0, z - pinH/2])
            pin();
        translate([tieL/4 - 5, 0, z - pinH/2])
            pin();
        translate([tieL/4 + 5, 0, z - pinH/2])
            pin();
    }
}

// --------------- ASSEMBLY ---------------
union() {
    // Tie structure
    union() {
        // Knot at top
        translate([0, 0, bodyH/2 + 15])
            knot();
        // Body
        translate([0, 0, bodyH/2])
            body();
    }

    // Shelves
    shelves();
}

