// Screw-shaped stool/bench with integrated threads
// Overall height: 120 mm, seat diameter: 100 mm
// 3D printable with 2 mm wall thickness, minimal overhangs

// Parameters
thread_height = 80;
thread_diameter = 100;
core_diameter = 60;
base_thickness = 15;
seat_thickness = 25;
thread_pitch = 15;
$fn = 32;

// Main module
module screw_stool() {
    // Central core cylinder
    cylinder(h=thread_height + base_thickness + seat_thickness, d=core_diameter, center=false);

    // Thread turns
    for(i = [0:thread_pitch:thread_height]) {
        translate([0, 0, base_thickness + i])
            thread_turn();
    }

    // Base plate
    translate([0, 0, 0])
        base_plate();

    // Seat top
    translate([0, 0, thread_height + base_thickness])
        seat_top();
}

// Single thread turn
module thread_turn() {
    difference() {
        // Outer thread ring
        cylinder(h=thread_pitch * 0.8, d=thread_diameter, center=true);

        // Hollow center
        cylinder(h=thread_pitch * 0.8 + 0.5, d=core_diameter - 1, center=true);

        // Cut diagonal for thread shape
        rotate([0, 0, 0])
            translate([0, 0, -thread_pitch/2])
                cube([thread_diameter + 2, thread_diameter + 2, thread_pitch], center=true);
    }
}

// Base plate with rounded edges
module base_plate() {
    difference() {
        // Main base
        cylinder(h=base_thickness, d=thread_diameter + 10, center=false);

        // Bottom chamfer
        translate([0, 0, -0.1])
            cylinder(h=base_thickness * 0.6, d1=thread_diameter + 10, d2=thread_diameter + 5, center=false);

        // Central hole for weight reduction
        cylinder(h=base_thickness + 0.2, d=core_diameter - 10, center=false);
    }
}

// Seat top with comfort features
module seat_top() {
    difference() {
        union() {
            // Main seat
            cylinder(h=seat_thickness, d=thread_diameter, center=false);

            // Top dome
            translate([0, 0, seat_thickness])
                scale([1, 1, 0.3])
                    sphere(d=thread_diameter);
        }

        // Central hole
        cylinder(h=seat_thickness + 5, d=core_diameter - 5, center=false);

        // Finger grip recesses around edge
        for(i = [0:45:359]) {
            rotate([0, 0, i])
                translate([thread_diameter/2 - 10, 0, seat_thickness - 5])
                    cylinder(h=5, d=8, center=true);
