base_thickness = 5;

// Wall thickness for 3D printing
wall_thickness = 2;

// Nail head module with furniture details
module nail_head() {
    // Main head disc
    difference() {
        cylinder(h=8, d=head_diameter, $fn=64);

        // Center cavity for weight reduction
        cylinder(h=10, d=head_diameter - 2*wall_thickness, $fn=64);

        // Decorative concentric rings on top
        for(i=[1:3]) {
            translate([0, 0, 8.1]) 
            difference() {
                cylinder(h=1, d=head_diameter - i*8, $fn=64);
                cylinder(h=2, d=head_diameter - i*8 - wall_thickness, $fn=64);
            }
        }
    }

    // Reinforcement ribs underneath head
    for(angle=[0:45:315]) {
        rotate([0, 0, angle])
        translate([0, head_diameter/2 - 5, -2])
        cube([wall_thickness*1.5, 8, 8]);
    }
}

// Thread pattern on shaft
module thread_profile() {
    // Single thread ridge
    linear_extrude(height=2, twist=30)
    difference() {
        circle(d=shaft_diameter + 2, $fn=64);
        circle(d=shaft_diameter, $fn=64);
    }
}

// Shaft module with furniture base
module nail_shaft() {
    // Main shaft tube
    difference() {
        cylinder(h=nail_height - 8, d=shaft_diameter, $fn=64);
        cylinder(h=nail_height - 6, d=shaft_diameter - 2*wall_thickness, $fn=64);
    }

    // Thread details on shaft
    for(z=[0:4:nail_height-16]) {
        translate([0, 0, z + 8])
        thread_profile();
    }

    // Furniture base plate for stability
    translate([0, 0, -base_thickness])
    difference() {
        cylinder(h=base_thickness, d=shaft_diameter + 15, $fn=64);
        cylinder(h=base_thickness + 1, d=shaft_diameter, $fn=64);
    }

    // Base reinforcement ribs
    for(angle=[0:60:300]) {
        rotate([0, 0, angle])
        translate([0, (shaft_diameter + 15)/2 - 3, -base_thickness])
        cube([wall_thickness, 6, base_thickness]);
    }
}

// Assembly
union() {
    // Nail head (seat)
    nail_head();

    // Shaft (support)
    translate([0, 0, 8])
    nail_shaft();
}

