                translate([0, 0, 17.5 - wall_thickness/2])
                    cube([35, 20, wall_thickness], center=true);

                // Screw holes (2 holes)
                for(i = [-1, 1]) {
                    translate([10, i * 7.5, 0])
                        rotate([90, 0, 0])
                            cylinder(h=wall_thickness+1, r=2.5, center=true, $fn=16);
                }
            }

            // Reinforcement ribs
            for(i = [0, 1]) {
                translate([12.5 + i * 10, 0, 17.5 - wall_thickness - 3])
                    cube([5, 20, 6], center=true);
            }
