// Airplane Bookshelf - a small wall-mounted shelf shaped like a classic propeller plane
// Designed for 3D printing with minimal supports and 2mm wall thickness

wall_thickness = 2;
scale_factor = 0.8;  // Overall size scaling

// Main dimensions (mm)
fuselage_length = 120 * scale_factor;
fuselage_width = 18 * scale_factor;
fuselage_height = 25 * scale_factor;

wing_span = 80 * scale_factor;
wing_chord = 25 * scale_factor;
wing_thickness = 4 * scale_factor;

tail_height = 30 * scale_factor;
tail_span = 35 * scale_factor;

prop_diameter = 20 * scale_factor;

module main_assembly() {
    // Fuselage (main body) - hollow for shelf space
    difference() {
        // Outer shell
        hull() {
            cube([fuselage_length, fuselage_width, fuselage_height], center=true);
            translate([fuselage_length/2 - 10, 0, 0])
                sphere(d=fuselage_width);
        }

        // Hollow interior
        translate([0, 0, -wall_thickness])
            cube([fuselage_length - 2*wall_thickness, fuselage_width - 2*wall_thickness, fuselage_height + 2*wall_thickness], center=true);
    }

    // Wings - structural support and shelf surfaces
    translate([fuselage_length/4, 0, -fuselage_height/2 + wing_thickness/2]) {
        // Main wings
        difference() {
            cube([wing_chord, wing_span, wing_thickness], center=true);
            // Cut holes for weight reduction
            for(i = [-1:1]) {
                translate([0, i * 20, 0])
                    cylinder(h=wing_thickness+1, d=8, center=true);
            }
        }
    }

    // Horizontal stabilizer
    translate([-fuselage_length/2 + 5, 0, fuselage_height/2])
        cube([15 * scale_factor, tail_span, 3 * scale_factor], center=true);

    // Vertical stabilizer (tail fin)
    translate([-fuselage_length/2 + 5, 0, fuselage_height/2 + 10 * scale_factor])
        rotate([90, 0, 0])
            cube([3 * scale_factor, tail_height, 15 * scale_factor], center=true);

    // Propeller hub
    translate([fuselage_length/2 + 5, 0, 0])
        cylinder(h=5 * scale_factor, d=prop_diameter/2, center=true);

    // Propeller blades
    for(i = [0:3]) {
        rotate([0, 0, i * 90])
            translate([fuselage_length/2 + 8, 0, 0])
                rotate([0, 90, 0])
                    cube([prop_diameter/2, 2 * scale_factor, 8 * scale_factor], center=true);
    }

    // Landing gear struts (decorative bookshelf supports)
    translate([fuselage_length/4, fuselage_width/2 + 2, -fuselage_height/2 - 5])
        cylinder(h=10 * scale_factor, d=3 * scale_factor);

    translate([fuselage_length/4, -fuselage_width/2 - 2, -fuselage_height/2 - 5])
        cylinder(h=10 * scale_factor, d=3 * scale_factor);

    // Mounting holes for wall attachment
    translate([0, 0, fuselage_height/2 + 1])
        for(pos = [-fuselage_length/3, fuselage_length/3]) {
            translate([pos, 0, 0])
                cylinder(h=wall_thickness + 2, d=4, center=true);
        }
}

// Add subtle surface details
