module rounded_cube(size, radius) {
    // Create rounded rectangle body
    hull() {
        for(x = [radius, size[0]-radius])
            for(y = [radius, size[1]-radius])
                translate([x, y, radius]) sphere(r=radius);
        for(x = [radius, size[0]-radius])
            for(y = [radius, size[1]-radius])
                translate([x, y, size[2]-radius]) sphere(r=radius);
    }
