// 3D-printable bicycle-shaped desk organizer
// Wall thickness: 2 mm, max dimension â‰ˆ 100 mm

$fn = 32;               // smooth curves
wall = 2;               // wall thickness for printability
scaleFactor = 0.7;      // scale to ~100 mm

module tubeProfile()        // simple hollow tube section
    difference() {
        circle(d = 8);
        circle(d = 8 - wall*2);
    }

module frameTube(l)         // hollow cylinder with length l
    linear_extrude(height = l, center = true)
        tubeProfile();

module wheel() {            // spoked wheel with hollow rim
    difference() {
        cylinder(h = 8, d = 36, center = true);           // tyre
        translate([0,0,-1])
            cylinder(h = 10, d = 32, center = true);      // remove inner
    }
    // 8 spokes
    for(a=[0:45:359])
        rotate([0,0,a])
            translate([10,0,0])
                cube([20, 2, 8], center = true);
}

module pedal()              // simple block pedal
    cube([12, 6, 4], center = true);

module crank()              // crank arm with pedal
    union() {
        translate([0,0,-8])
            rotate([90,0,0])
                frameTube(22);
        translate([22,0,-8])
            pedal();
    }

module chainring()          // 5-arm chainring
    difference() {
        cylinder(h = 4, d = 32, center = true);
        cylinder(h = 5, d = 26, center = true);
    }
    for(a=[0:72:359])
        rotate([0,0,a])
            translate([6,0,0])
                cube([12, 3, 4], center = true);

module handlebar()          // flat bar with stem
    union() {
        translate([0,0,10])
            rotate([90,0,0])
                frameTube(50);
        rotate([0,90,0])
            frameTube(30);
    }

module saddle()             // seat shell
    difference() {
        scale([1, 1.3, 0.5])
            sphere(d = 20);
        translate([0,0,-5])
            cube([40, 40, 10], center = true);
    }

module bicycleOrganizer() {
    // rear triangle
    translate([0,0,5])
        rotate([90,0,0])
            frameTube(40);                  // seat tube
    translate([-20,0,-5])
        rotate([90,-10,0])
            frameTube(45);                  // down tube
    translate([-20,0,-15])
        rotate([0,-10,0])
        rotate([0,-45,0])
            frameTube(50);                  // chain stay
    translate([20,0,5])
        rotate([0,-10,0])
        rotate([0,-45,0])
            frameTube(50);                  // seat stay

    // fork
    translate([0,0,-25])
        rotate([90,0,0])
            frameTube(45);

    // wheels
    translate([0,0,35])
        wheel();
    translate([0,0,-35])
        wheel();

    // cranks & chainring
    translate([0,0,-15])
        chainring();
    translate([0,0,-15])
        rotate([0,0,30])
            crank();
    translate([0,0,-15])
        rotate([0,0,210])
            crank();

    // handlebar & stem
    translate([0,0,-25])
        handlebar();

    // saddle
    translate([0,0,15])
        saddle();

    // internal compartments (difference)
    translate([0,0,5])
        rotate([90,0,0])
            cylinder(h = 42, d = 15, center = true);
    translate([0,0,-15])
        cube([20, 20, 60], center = true);
}

scale([scaleFactor, scaleFactor, scaleFactor])
    bicycleOrganizer();

