// Helper: rounded box
module rounded_box(l, w, h, r) {
    translate([0,0,r]) hull() {
        translate([ l/2-r,  w/2-r, 0]) cylinder(h=h, r=r);
        translate([ l/2-r, -w/2+r, 0]) cylinder(h=h, r=r);
        translate([-l/2+r,  w/2-r, 0]) cylinder(h=h, r=r);
        translate([-l/2+r, -w/2+r, 0]) cylinder(h=h, r=r);
    }
}

// Helper: canoe-shaped profile
module canoe_profile() {
    difference() {
        // Full hull
        scale([1, 0.45, 0.4]) rotate([90,0,0]) 
            cylinder(h=hull_length, d=hull_width*2, center=true);
        // Hollow interior offset by wall
        translate([0,0,-wall]) 
            scale([1, 0.42, 0.38]) rotate([90,0,0]) 
                cylinder(h=hull_length+wall*2, d=(hull_width-wall)*2, center=true);
    }
}

// Main hull
module hull() {
    difference() {
        canoe_profile();
        // Remove sharp ends for printing
        translate([ hull_length/2+wall, 0, 0]) rotate([0,90,0]) 
            cylinder(h=hull_width*2, d=hull_width, center=true);
        translate([-hull_length/2-wall, 0, 0]) rotate([0,90,0]) 
            cylinder(h=hull_width*2, d=hull_width, center=true);
    }
}

