shelf_depth = 25;
shelf_thickness = 3;

// Propeller parameters
prop_diameter = 20;
prop_blades = 3;

// Fins
fin_height = 15;
fin_length = 30;

// Create the main airship hull (ellipsoid)
module hull() {
    difference() {
        // Main ellipsoid body
        scale([1, 0.6, 0.4])
            sphere(d=body_diameter);

        // Hollow out interior
        translate([0, 0, 2])
            scale([1, 0.55, 0.35])
                sphere(d=body_diameter - wall_thickness*2);
    }
}

// Create gondola shelf structure
module gondola() {
    // Main shelf box
    translate([0, 0, -body_diameter/2 - shelf_thickness - 5])
    difference() {
        cube([shelf_width, shelf_depth, shelf_thickness], center=true);

        // Hollow interior for books
        cube([shelf_width - wall_thickness*2, shelf_depth - wall_thickness, shelf_thickness - wall_thickness], center=true);
    }

    // Support cables (simplified as cylinders)
    for(x = [-shelf_width/2 + 5, shelf_width/2 - 5])
        for(y = [-shelf_depth/2 + 5, shelf_depth/2 - 5])
            translate([x, y, -body_diameter/2 - shelf_thickness/2 - 2.5])
                cylinder(h=5, r=1, center=true);
}

// Create tail fins
module tail_fins() {
    // Vertical stabilizer
    translate([-body_length/2 + 5, 0, body_diameter/4])
        rotate([0, 10, 0])
            difference() {
                cube([fin_length, wall_thickness*2, fin_height], center=true);
                cube([fin_length - 2, wall_thickness*2, fin_height - 2], center=true);
            }

    // Horizontal stabilizers (top and bottom)
    for(z = [body_diameter/4, -body_diameter/4])
        translate([-body_length/2 + 5, 0, z])
            rotate([10, 0, 0])
                difference() {
                    cube([fin_length, fin_height, wall_thickness*2], center=true);
                    cube([fin_length - 2, fin_height - 2, wall_thickness*2], center=true);
                }
}

// Create propeller assembly
module propeller() {
    translate([body_length/2 - 5, 0, 0])
        rotate([90, 0, 0])
            union() {
                // Hub
                cylinder(h=4, r=3, center=true);

                // Propeller blades
                for(i = [0:prop_blades-1]) {
                    rotate([0, 0, i*360/prop_blades])
                        translate([0, prop_diameter/4, 0])
                            scale([1, 3, 0.5])
                                sphere(d=5);
                }
            }
}

// Create mooring ring (top mounting point)
module mooring_ring() {
    translate([0, 0, body_diameter/2 + 3])
        difference() {
            cylinder(h=wall_thickness*2, r=5, center=true);
            cylinder(h=wall_thickness*2 + 1, r=3, center=true);
        }
}

// Assemble the complete airship bookshelf
union() {
    // Main hull
    hull();

    // Gondola shelf
    gondola();

    // Tail fins
    tail_fins();

    // Propeller
    propeller();

    // Mooring ring for hanging
    mooring_ring();
}

// Zeppelin Bookshelf - A wall-mounted bookshelf shaped like a classic airship
// Size: 150mm long, 60mm tall, 40mm deep - suitable for small books or decor

module zeppelin_bookshelf() {
    hull_thickness = 2;
    shelf_depth = 40;

    // Main zeppelin envelope (body)
    difference() {
        // Outer envelope
        scale([1, 0.6, 0.4])
            sphere(d=150, $fn=100);

        // Hollow interior
        translate([0, 0, -hull_thickness])
            scale([1 - (hull_thickness*2/150), 0.6 - (hull_thickness*2/90), 0.4 - (hull_thickness*2/60)])
                sphere(d=150 - hull_thickness*2, $fn=100);

        // Flat back for wall mounting
        translate([-75 - hull_thickness, -45, -30])
            cube([hull_thickness, 90, 60]);
    }

    // Gondola (passenger cabin) - serves as bottom shelf
    translate([0, -25, -25]) {
        difference() {
            // Main gondola body
            cube([80, 15, 20], center=true);

            // Hollow interior
            translate([0, 0, hull_thickness])
                cube([80 - hull_thickness*2, 15 - hull_thickness*2, 20 - hull_thickness*2], center=true);
        }
    }

    // Vertical support fins (structural)
    for (i = [-1, 1]) {
        translate([i * 50, 0, 0]) {
            rotate([0, 90, 0])
                linear_extrude(height=hull_thickness)
                    polygon(points=[
                        [0, -20], [10, 0], [0, 20], [-5, 0]
                    ]);
        }
    }

    // Horizontal stabilizers (shelves)
    module stabilizer_shelf(offset, width) {
        translate([offset, 0, 0]) {
            rotate([0, 90, 0])
                linear_extrude(height=hull_thickness)
                    polygon(points=[
                        [-width/2, -15], [width/2, -15], [width/2-5, 15], [-width/2+5, 15]
                    ]);
        }
    }

    stabilizer_shelf(45, 25);
    stabilizer_shelf(-45, 25);

    // Wall mounting brackets
    translate([-75, 0, 0]) {
        for (i = [-1, 0, 1]) {
            translate([0, i * 20, 0]) {
                difference() {
                    // Bracket body
                    rotate([0, 90, 0])
                        cylinder(h=10, d=8, center=true);

                    // Screw hole
                    rotate([0, 90, 0])
                        cylinder(h=11, d=4, center=true);
                }
            }
        }
    }

    // Decorative rivets along the envelope
    module rivet_ring(z_pos, count) {
        for (i = [0:count-1]) {
            angle = i * 360/count;
            translate([
                75 * cos(angle),
                45 * 0.6 * sin(angle),
                z_pos
            ]) {
                sphere(d=3, $fn=8);
            }
        }
    }

    // Add rivets at different z positions
    rivet_ring(20, 8);
    rivet_ring(0, 12);
    rivet_ring(-20, 8);
}

// Generate the complete bookshelf
zeppelin_bookshelf();

