// Seaplane Bookshelf - Medium complexity realistic style
// Overall dimensions: 120mm wingspan, 60mm height, 90mm length

$fn = 32;  // Smooth curves for 3D printing

// Main parameters
fuselage_length = 80;
fuselage_width = 25;
fuselage_height = 20;
wing_span = 120;
wing_thickness = 4;
float_width = 15;
float_height = 12;

module seaplane_bookshelf() {
    union() {
        // Fuselage - main bookshelf body
        translate([0, 0, float_height + 5]) {
            difference() {
                // Main fuselage
                cube([fuselage_length, fuselage_width, fuselage_height], center = true);

                // Shelf cavities (3 levels)
                for(i = [0:2]) {
                    translate([-fuselage_length/2 + 15 + i*25, 0, -fuselage_height/2 + 2]) {
                        cube([20, fuselage_width - 4, fuselage_height - 4], center = true);
                    }
                }
            }
        }

        // Wings - side shelves
        module wing() {
            difference() {
                // Main wing shape
                translate([0, 0, 0]) {
                    scale([1, 1, 0.3]) sphere(r = wing_span/2);
                    cube([wing_span, wing_thickness, wing_span/4], center = true);
                }

                // Cut to make flat shelf surface
                translate([0, 0, -wing_span/8]) {
                    cube([wing_span + 2, wing_thickness + 2, wing_span/4], center = true);
                }
            }
        }

        // Left wing
        translate([-fuselage_length/2 + 10, 0, float_height + 15]) {
            rotate([0, 0, 90]) wing();
        }

        // Right wing
        translate([fuselage_length/2 - 10, 0, float_height + 15]) {
            rotate([0, 0, 90]) wing();
        }

        // Floats - base supports
        module float() {
            hull() {
                // Front float section
                translate([0, 0, 0]) {
                    scale([1, 0.7, 1]) sphere(r = float_width);
                }

                // Rear float section
                translate([float_width*1.5, 0, 0]) {
                    scale([1, 0.7, 0.8]) sphere(r = float_width);
                }

                // Connecting body
                cube([float_width*2.5, float_width*0.7, float_height], center = true);
            }
        }

        // Front float
        translate([-20, 0, 0]) {
            float();
        }

        // Rear float
        translate([20, 0, 0]) {
            float();
        }

        // Tail fin - rear bookshelf support
        translate([fuselage_length/2 - 5, 0, float_height + 5 + fuselage_height/2]) {
            rotate([0, 90, 0]) {
                difference() {
                    // Main tail
                    cube([15, 25, 3], center = true);

                    // Cutout for style
                    translate([0, 5, 0]) {
                        cube([10, 10, 4], center = true);
                    }
                }
            }
        }

        // Horizontal stabilizer - small shelf
        translate([fuselage_length/2, 0, float_height + 15]) {
            rotate([90, 0, 0]) {
                cube([30, wing_thickness, 15], center = true);
            }
        }

        // Propeller support - front bookend
        translate([-fuselage_length/2 - 15, 0, float_height + 5]) {
            cylinder(h = 10, r = 5, center = false);
            translate([0, 0, 10]) {
                rotate([0, 0, 45]) {
                    for(i = [0:3]) {
                        rotate([0, 0, i*90]) {
                            cube([20, 1.5, 3], center = true);
                        }
                    }
                }
            }
        }

        // Cockpit window - decorative element
        translate([-fuselage_length/2 + 20, 0, float_height + 5 + fuselage_height/2]) {
            sphere(r = 8);
        }

        // Wing struts - structural supports
        module strut() {
            cylinder(h = 15, r = 1.5, center = false);
        }

        // Left wing struts
        translate([-fuselage_length/2 + 10, -wing_span/2 + 20, float_height + 10]) {
            strut();
        }
        translate([-fuselage_length/2 + 10, wing_span/2 - 20, float_height + 10]) {
            strut();
        }

        // Right wing struts
        translate([fuselage_length/2 - 10, -wing_span/2 + 20, float_height + 10]) {
            strut();
        }
        translate([fuselage_length/2 - 10, wing_span/2 - 20, float_height + 10]) {
            strut();
        }
    }
}

