// Destroyer-style bookshelf (lean, angular, naval-inspired)
// Overall footprint ~110 mm x 45 mm x 170 mm (L x D x H)

// Parameters
wall = 2;            // 3D-printable wall thickness
shelf_h = 30;        // vertical spacing per shelf
posts = 4;           // number of vertical posts
post_r = 5;          // radius of cylindrical posts
deck_d = 30;         // depth of each shelf deck
deck_w = 100;        // width of each shelf deck
clear = 0.2;         // clearance for tight fits

module post(h = 170) {
    // Vertical destroyer-style â€œmastâ€ post with slight taper
    cylinder(h1 = h, r1 = post_r, h2 = h, r2 = post_r * 0.9, $fn = 16);
}

module shelf_plank(w = deck_w, d = deck_d, t = wall) {
    // Thin, angular plank with chamfered edges
    difference() {
        cube([w, d, t], center = true);
        translate([0, 0, t/2 + clear])
            cube([w - wall*2, d - wall*2, t + clear*2], center = true);
    }
}

module frame_assembly() {
    // Assemble four corner posts
    for(x = [0, 1])
        for(y = [0, 1]) {
            px = x * (deck_w - post_r*2) - (deck_w - post_r*2)/2;
            py = y * (deck_d - post_r*2) - (deck_d - post_r*2)/2;
            translate([px, py, 0])
                post();
        }
}

module deck_assembly(count = 5) {
    // Horizontal shelves (â€œdecksâ€) evenly spaced
    for(i = [0:count-1]) {
        z = i * shelf_h + wall;
        translate([0, 0, z])
            shelf_plank();
    }
}

module gun_turret() {
    // Stylized gun turret on top shelf (furniture accent)
    rotate([90, 0, 0])
        cylinder(h = 12, r1 = 8, r2 = 6, $fn = 20);
}

module railing() {
    // Simple railing along back edge (structural + aesthetic)
    linear_extrude(height = 6)
        square([deck_w - wall*2, wall], center = true);
}

// Main model
union() {
    frame_assembly();
    deck_assembly(5);

    // Top turret detail
    translate([0, 0, 5 * shelf_h + wall + 6])
        gun_turret();

    // Rear railings on each deck
    for(i = [0:4]) {
        z = i * shelf_h + wall;
        translate([0, deck_d/2 - wall/2, z + wall])
            railing();
    }
}

