    difference() {
        hull_shape();

        // Remove bottom for flat printing surface
        translate([0, 0, -1])
            cube([hull_length + 10, hull_width + 10, 2], center = true);
    }

    // Deck
    translate([0, 0, hull_height])
        deck();

    // Cabin structure
    translate([-hull_length/4, 0, hull_height])
        cabin();

    // Masts
    translate([hull_length/3, 0, hull_height])
        mast(mast_height);
    translate([-hull_length/3, 0, hull_height])
        mast(mast_height * 0.8);

    // Cannons along sides
    for(i = [-1:1]) {
        translate([i * 30, hull_width/2 + 3, hull_height - 5])
            rotate([0, 90, 0])
                cannon();
        translate([i * 30, -hull_width/2 - 3, hull_height - 5])
            rotate([0, -90, 0])
                cannon();
    }

    // Anchor
    translate([hull_length/2 - 5, 0, hull_height - 3])
        anchor();
}

module hull_shape() {
    // Main hull with curved bottom
    hull() {
        // Bow
        translate([hull_length/2 - 5, 0, hull_height/2])
            sphere([5, hull_width/2 - 2, hull_height/2]);

        // Stern
        translate([-hull_length/2 + 5, 0, hull_height/2])
            sphere([5, hull_width/2 - 2, hull_height/2]);

        // Mid section
        translate([0, 0, hull_height/2])
            cube([hull_length - 20, hull_width - 4, hull_height], center = true);
    }
}

module deck() {
    // Main deck with planking texture effect
    difference() {
        // Base deck
        translate([0, 0, 1])
            cube([hull_length - 10, hull_width - 4, 2], center = true);

        // Plank grooves (for printing texture)
        for(i = [-hull_width/2 + 3 : 3 : hull_width/2 - 3]) {
            translate([0, i, 0])
                cube([hull_length - 12, 1, 4], center = true);
        }
    }
}

module cabin() {
    // Captain's cabin structure
    difference() {
        union() {
            // Cabin walls
            translate([0, 0, cabin_height/2])
                cube([hull_length/3, hull_width - 8, cabin_height], center = true);

            // Cabin roof
            translate([0, 0, cabin_height + 1])
                cube([hull_length/3 + 2, hull_width - 6, 2], center = true);

            // Windows
            for(i = [-1:1]) {
                translate([0, hull_width/2 - 6, i * 3 + 6])
                    cube([hull_length/3 - 4, 2, 4], center = true);
            }
        }

        // Window openings
        for(i = [-1:1]) {
            translate([0, hull_width/2 - 5, i * 3 + 6])
                cube([hull_length/3 - 2, 3, 4], center = true);
        }
    }
}

module mast(height) {
    // Mast with cross spars
    difference() {
        union() {
            // Main mast pole
            cylinder(h = height, r1 = 1.5, r2 = 1);

            // Cross spars
            translate([0, 0, height * 0.6])
                rotate([90, 0, 0])
                    cylinder(h = hull_width * 1.5, r = 0.8);
            translate([0, 0, height * 0.4])
                rotate([90, 0, 0])
                    cylinder(h = hull_width * 1.2, r = 0.6);
        }

        // Remove base for clean connection
        translate([0, 0, -1])
            cylinder(h = 2, r = 2);
    }
}

module cannon() {
