        // Barrel
        cylinder(h = 8, r1 = 1.5, r2 = 1.2);

        // Base
        translate([0, 0, -2])
            cylinder(h = 2, r = 2.5);

        // Wheels
        for(side = [-1:2:1]) {
            translate([side * 2.5, 0, -1])
                rotate([0, 90, 0])
                    cylinder(h = 1, r = 1.5);
        }
    }
}

module anchor() {
    // Anchor with furniture-style proportions
    union() {
        // Main shank
        cylinder(h = 6, r = 0.8);

        // Arms
        for(side = [-1:2:1]) {
            translate([0, side * 2, -2])
