tram_height = 35;

// Wall thickness for 3D printing
wall = 2;

// Main body module
module tram_body() {
    difference() {
        // Outer shell
        union() {
            // Main passenger compartment
            translate([0, 0, tram_height/2]) 
                cube([tram_length, tram_width, tram_height], center=true);

            // Driver cabin (tapered front)
            translate([tram_length/2 - 5, 0, tram_height/2 + 5]) 
                cube([20, tram_width, tram_height + 10], center=true);

            // Roof structure
            translate([0, 0, tram_height + 5]) 
                cube([tram_length + 10, tram_width - 10, 10], center=true);
        }

        // Hollow interior
        translate([0, 0, tram_height/2]) 
            cube([tram_length - wall*2, tram_width - wall*2, tram_height - wall], center=true);
    }
}

// Windows module
module windows() {
    for(i = [-2:1:2]) {
        // Side windows
        translate([i * 20, tram_width/2 - wall/2, tram_height/2 + 5]) 
            cube([15, wall, 12], center=true);
        translate([i * 20, -tram_width/2 + wall/2, tram_height/2 + 5]) 
            cube([15, wall, 12], center=true);

        // Driver cabin windows
        translate([tram_length/2 + 5, tram_width/4, tram_height/2 + 8]) 
            rotate([0, 0, 15])
            cube([12, wall, 10], center=true);
        translate([tram_length/2 + 5, -tram_width/4, tram_height/2 + 8]) 
            rotate([0, 0, -15])
            cube([12, wall, 10], center=true);
    }
}

// Wheels module
module wheels() {
    for(i = [-1, 1]) {
        // Wheel sets
        translate([-30, i * (tram_width/2 + 5), 5]) 
            cylinder(h=8, r=5, center=true);
        translate([30, i * (tram_width/2 + 5), 5]) 
            cylinder(h=8, r=5, center=true);

        // Bogie frames
        translate([-30, i * (tram_width/2 + 5), 2]) 
            cube([20, 8, 4], center=true);
        translate([30, i * (tram_width/2 + 5), 2]) 
            cube([20, 8, 4], center=true);
    }
}

// Pantograph (roof power connector)
module pantograph() {
    translate([0, 0, tram_height + 10]) {
        // Base
        cylinder(h=3, r=4);

        // Arms
        for(angle = [0:45:315]) {
            rotate([0, 0, angle])
            translate([0, 0, 1.5])
            cube([2, 12, 2], center=true);
        }

        // Contact bar
        translate([0, 0, 15])
        cube([30, 2, 2], center=true);
    }
}

// Interior furniture elements
module interior_furniture() {
    // Seats along walls
    for(side = [-1, 1]) {
        for(pos = [-40, -20, 0, 20, 40]) {
            translate([pos, side * (tram_width/2 - 8), tram_height/2 - 8])
            cube([15, 6, 8], center=true);
            translate([pos, side * (tram_width/2 - 12), tram_height/2 - 12])
            cube([15, 2, 4], center=true);
        }
    }

    // Central pole
    translate([0, 0, tram_height/2])
    cylinder(h=tram_height - 4, r=2, center=true);

    // Handrails
    for(side = [-1, 1]) {
        translate([0, side * (tram_width/2 - 4), tram_height/2 + 5])
        cube([tram_length - 10, 2, 2], center=true);
    }
}

// Connectors between trams
module coupler() {
    // Front coupler
    translate([tram_length/2 + 10, 0, 5])
    union() {
        cube([8, 6, 4], center=true);
        translate([4, 0, 0])
        cylinder(h=6, r=2, center=true);
    }

    // Rear coupler
    translate([-tram_length/2 - 10, 0, 5])
    mirror([1, 0, 0])
    union() {
        cube([8, 6, 4], center=true);
        translate([4, 0, 0])
        cylinder(h=6, r=2, center=true);
    }
}

// Assembly
union() {
    tram_body();
    windows();
    wheels();
    pantograph();
    coupler();

    // Interior visible through windows
    color("lightgray", 0.3)
    interior_furniture();
}

// Base for stability
translate([0, 0, -1])
cube([tram_length + 20, tram_width + 10, 2], center=true);

// Realistic Trolley Furniture
// Medium complexity OpenSCAD model for 3D printing

// Overall dimensions
trolley_width = 80;
trolley_depth = 60;
trolley_height = 100;
wall_thickness = 2;

// Wheel dimensions
wheel_radius = 10;
wheel_width = 5;
axle_diameter = 3;

// Handle dimensions
handle_height = 40;
handle_diameter = 8;

// Shelf spacing
shelf_spacing = 30;

// Main frame structure
module frame() {
    // Bottom frame rectangle
    difference() {
        cube([trolley_width, trolley_depth, wall_thickness], center=true);

        // Hollow center
        translate([0, 0, -1])
            cube([trolley_width-4, trolley_depth-4, wall_thickness+2], center=true);
    }

    // Side frames
    for(side = [-1, 1]) {
        translate([side * (trolley_width/2 - wall_thickness/2), 0, trolley_height/2 - wall_thickness/2])
            cube([wall_thickness, trolley_depth, trolley_height], center=true);
    }

    // Back frame
    translate([0, -trolley_depth/2 + wall_thickness/2, trolley_height/2 - wall_thickness/2])
        cube([trolley_width, wall_thickness, trolley_height], center=true);
}

// Shelves
module shelves() {
    for(i = [0:1:2]) {
        translate([0, 0, i * shelf_spacing + wall_thickness])
        difference() {
            cube([trolley_width - 4, trolley_depth - 4, wall_thickness], center=true);
            translate([0, 0, -1])
                cube([trolley_width - 8, trolley_depth - 8, wall_thickness+2], center=true);
        }
    }
}

// Wheels assembly
module wheels() {
    for(side_x = [-1, 1]) {
        for(side_y = [-1, 1]) {
            translate([side_x * (trolley_width/2 - wheel_radius - 5), 
                      side_y * (trolley_depth/2 - wheel_radius - 5), 
                      -wheel_radius - wall_thickness/2]) {

                // Wheel
                rotate([90, 0, 0])
                difference() {
                    cylinder(h=wheel_width, r=wheel_radius, center=true, $fn=20);
                    cylinder(h=wheel_width+2, r=axle_diameter/2, center=true, $fn=16);
                }

                // Axle
                rotate([90, 0, 0])
                    cylinder(h=trolley_depth/2 - wheel_radius - 5, r=axle_diameter/2, center=false, $fn=16);
            }
        }
    }
}

// Handle
module handle() {
    translate([0, trolley_depth/2 - wall_thickness, trolley_height - handle_height/2])
    union() {
        // Vertical posts
        for(side = [-1, 1]) {
            translate([side * (trolley_width/2 - 20), 0, 0])
                cylinder(h=handle_height, r=handle_diameter/2, center=true, $fn=12);
        }

        // Horizontal grip
        translate([0, 0, handle_height/2])
            rotate([90, 0, 0])
                cylinder(h=trolley_width - 40, r=handle_diameter/2, center=true, $fn=12);
    }
}

// Assembly
union() {
    // Main structure
    frame();
    shelves();

    // Wheels
    wheels();

    // Handle
    handle();
}

