// Drawer module
module drawer(width, height, depth, handle_width) {
    difference() {
        // Drawer box
        translate([0, 0, 0])
            cube([width, depth, height]);

        // Hollow interior
        translate([wall_thickness, wall_thickness, wall_thickness])
            cube([width - 2*wall_thickness, depth - wall_thickness, height - 2*wall_thickness]);
