// Bulldozer Furniture - Realistic Style
// Medium complexity 3D printable model

// Main dimensions (mm)
track_width = 14;
track_length = 80;
track_height = 10;
body_width = 50;
body_length = 70;
body_height = 35;
blade_width = 80;
blade_height = 35;
blade_thickness = 3;

// Track module with tread pattern
module track_segment(width, length, height) {
    difference() {
        // Main track body
        cube([length, width, height], center=true);

        // Tread cuts
        for(i = [-length/2+5 : 5 : length/2-5]) {
            translate([i, 0, height/2-1])
                cube([3, width+2, 2], center=true);
        }
    }
}

// Track assembly with supports
module track_assembly(pos_x, pos_y) {
    translate([pos_x, pos_y, track_height/2]) {
        // Track
        track_segment(track_width, track_length, track_height);

        // Track supports
        for(i = [-track_length/2+10 : 15 : track_length/2-10]) {
            translate([i, 0, track_height/2+2])
                cube([6, track_width-4, 4], center=true);
        }
    }
}

// Main body with structural details
module main_body() {
    difference() {
        // Main body block
        translate([0, 0, body_height/2+track_height])
            cube([body_length, body_width, body_height], center=true);

        // Cabin cutout
        translate([body_length/4, 0, body_height+track_height-15])
            cube([body_length/3, body_width-10, 20], center=true);

        // Engine vents
        for(i = [body_length/2-20 : 5 : body_length/2-5]) {
            translate([i, body_width/2-1, body_height/2+track_height])
                cube([2, 2, 10], center=true);
        }
    }

    // Cabin roof
    translate([body_length/4, 0, body_height+track_height])
        cube([body_length/3+4, body_width-6, 3], center=true);

    // Exhaust pipe
    translate([body_length/2-10, body_width/2-5, body_height+track_height+3])
        cylinder(h=15, r=3, $fn=16);
}

// Blade assembly with realistic curves
module blade() {
    // Main blade
    difference() {
        translate([-body_length/2-blade_thickness/2-5, 0, blade_height/2+track_height])
            cube([blade_thickness, blade_width, blade_height], center=true);

        // Blade curve
        translate([-body_length/2-blade_thickness/2-5-blade_thickness, 0, blade_height/2+track_height])
            cylinder(h=blade_width+2, r=blade_height/2, $fn=50);
    }

    // Blade supports
    translate([-body_length/2-5, 0, track_height+10])
        cube([10, 8, 20], center=true);

    // Hydraulic cylinders
    translate([-body_length/2-15, blade_width/4, track_height+blade_height/2])
        cylinder(h=20, r=3, $fn=12);
    translate([-body_length/2-15, -blade_width/4, track_height+blade_height/2])
        cylinder(h=20, r=3, $fn=12);
}

// Assembly
union() {
    // Left track
    track_assembly(0, -body_width/2-track_width/2-2);

    // Right track
    track_assembly(0, body_width/2+track_width/2+2);

    // Main body
    main_body();

    // Blade
    blade();

    // Additional structural details
    // Front lights
    translate([body_length/2-5, body_width/3, track_height+body_height-5])
        sphere(r=2.5);
    translate([body_length/2-5, -body_width/3, track_height+body_height-5])
        sphere(r=2.5);

