// Skateboard Chair - Realistic Style
// A miniature chair that incorporates an actual skateboard deck as the seat
// Printable scale: approx 100mm tall

$fn=50;

// Overall dimensions
chair_height = 100;
seat_width = 50;
seat_length = 120;
leg_thickness = 3;

// Skateboard deck parameters
deck_length = 60;
deck_width = 20;
deck_thickness = 2;
truck_height = 3;
wheel_diameter = 6;

// Chair back parameters
back_height = 40;
back_angle = 15;

// Module for skateboard deck with trucks
module skateboard_deck() {
    // Main deck
    translate([0, 0, deck_thickness/2])
    scale([1, 0.9, 1])
    minkowski() {
        cube([deck_length, deck_width, deck_thickness], center=true);
        cylinder(h=0.1, r=3);
    }

    // Trucks
    translate([deck_length/3, 0, 0])
    cube([3, deck_width - 4, truck_height], center=true);

    translate([-deck_length/3, 0, 0])
    cube([3, deck_width - 4, truck_height], center=true);

    // Wheels
    for(x=[deck_length/3, -deck_length/3])
        for(y=[deck_width/2 - 2, -deck_width/2 + 2])
            translate([x, y, -wheel_diameter/2])
            cylinder(h=wheel_diameter, r=wheel_diameter/2, center=true);
}

// Chair legs with reinforcement
module chair_leg() {
    difference() {
        // Main leg
        cube([leg_thickness, leg_thickness, chair_height - deck_thickness], center=true);

        // Lightening holes
        for(i=[1:3])
            translate([0, 0, -chair_height/2 + 15*i])
            cylinder(h=leg_thickness+1, r=leg_thickness/2, center=true);
    }
}

// Back support structure
module back_support() {
    // Vertical posts
    translate([seat_length/2 - 5, 0, back_height/2 + deck_thickness])
    rotate([back_angle, 0, 0])
    cube([leg_thickness, seat_width - 10, back_height], center=true);

    translate([-seat_length/2 + 5, 0, back_height/2 + deck_thickness])
    rotate([back_angle, 0, 0])
    cube([leg_thickness, seat_width - 10, back_height], center=true);

    // Horizontal brace
    translate([0, 0, back_height + deck_thickness - 5])
    rotate([back_angle, 0, 0])
    cube([seat_length - 10, leg_thickness, leg_thickness], center=true);
}

// Assembly
union() {
    // Seat - using skateboard deck
    translate([0, 0, chair_height - deck_thickness/2])
    skateboard_deck();

    // Front legs
    translate([seat_length/2 - 5, seat_width/2 - 5, 0])
    chair_leg();
    translate([seat_length/2 - 5, -seat_width/2 + 5, 0])
    chair_leg();
    translate([-seat_length/2 + 5, seat_width/2 - 5, 0])
    chair_leg();
    translate([-seat_length/2 + 5, -seat_width/2 + 5, 0])
    chair_leg();

    // Back support
    back_support();

    // Cross bracing
    translate([0, seat_width/2 - leg_thickness/2, chair_height/2])
    cube([seat_length - 10, leg_thickness, chair_height - deck_thickness], center=true);

    translate([0, -seat_width/2 + leg_thickness/2, chair_height/2])
    cube([seat_length - 10, leg_thickness, chair_height - deck_thickness], center=true);
}

// Longboard Furniture - A bookshelf shaped like a skateboard deck
// Dimensions: ~120mm length, ~30mm width, ~45mm height
// Features: Hollow shelf area, realistic deck shape, truck mounts as structural supports

$fn = 32;  // Smooth curves

// Overall dimensions
deck_length = 120;
deck_width = 30;
deck_thickness = 4;
truck_height = 8;
wheel_diameter = 12;
wheel_width = 8;

// Main deck with taper
module deck() {
    // Tapered deck shape - wider in middle, narrower at ends
    hull() {
        // Front rounded end
        translate([deck_length/2 - deck_width/2, 0, deck_thickness/2])
            cylinder(h = deck_thickness, r = deck_width/2);

        // Rear rounded end
        translate([-deck_length/2 + deck_width/2, 0, deck_thickness/2])
            cylinder(h = deck_thickness, r = deck_width/2);

        // Middle section with taper
        for (x = [-deck_length/2 + deck_width/2 + 2 : 2 : deck_length/2 - deck_width/2 - 2]) {
            w = deck_width * (1 - 0.3 * abs(x) / (deck_length/2));
            translate([x, 0, deck_thickness/2])
                cube([2, w, deck_thickness], center = true);
        }
    }
}

// Hollow out the deck for bookshelf functionality
module hollow_deck() {
    difference() {
        deck();

        // Hollow interior with 2mm walls
        translate([0, 0, -1])
            scale([0.85, 0.85, 0.8])
                deck();

        // Front opening
        translate([deck_length/2 - 1, 0, deck_thickness/2])
            cube([2, deck_width * 0.7, deck_thickness + 2], center = true);

        // Rear opening
        translate([-deck_length/2 + 1, 0, deck_thickness/2])
            cube([2, deck_width * 0.7, deck_thickness + 2], center = true);
    }
}

// Truck base as structural support
module truck_base() {
    // Main truck block
    cube([deck_width * 0.8, 10, truck_height], center = true);

    // Bolt holes for realistic detail
    for (x = [-15, -5, 5, 15]) {
        for (y = [-3, 3]) {
            translate([x, y, -truck_height/2 - 1])
                cylinder(h = truck_height + 2, r = 1);
        }
    }
}

// Wheels as feet
module wheels() {
    for (side = [-1, 1]) {
        translate([0, side * (deck_width/2 + wheel_width/2), -wheel_diameter/2 - truck_height])
            rotate([0, 90, 0])
                cylinder(h = wheel_width, r = wheel_diameter/2);
    }
}

// Complete truck assembly
module truck_assembly() {
    translate([0, 0, -truck_height/2]) {
        union() {
            truck_base();
            wheels();
        }
    }
}

// Grip tape texture pattern
module grip_pattern() {
    for (x = [-deck_length/2 + 5 : 5 : deck_length/2 - 5]) {
        for (y = [-deck_width/2 + 3 : 3 : deck_width/2 - 3]) {
            translate([x, y, deck_thickness + 0.5])
                cube([1, 1, 0.5], center = true);
        }
    }
}

// Main assembly
union() {
    // Main deck shelf
    hollow_deck();

    // Front truck support
    translate([deck_length/2 - 20, 0, 0])
        truck_assembly();

    // Rear truck support
    translate([-deck_length/2 + 20, 0, 0])
        truck_assembly();

    // Grip tape texture on top
    grip_pattern();
}

