gondola_width = 35;
gondola_height = 45;

// Wall thickness for 3D printing
wall_thickness = 2;

// Main hull module
module gondola_hull() {
    difference() {
        // Main hull shape - elongated ellipsoid
        scale([1, 0.4, 0.3])
            sphere(d = gondola_length);

        // Hollow out the interior
        translate([0, 0, -5])
            scale([1, 0.35, 0.25])
                sphere(d = gondola_length - wall_thickness*2);

        // Flat bottom for stability
        translate([0, 0, -gondola_height/2 + 2])
            cube([gondola_length + 10, gondola_width + 10, 10], center = true);
    }
}

// Raised prow (ferro) decorative element
module ferro_prow() {
    translate([-gondola_length/2 + 5, 0, -gondola_height/2 + 10]) {
        // Base structure
        cylinder(h = 15, r1 = 8, r2 = 2);

        // Decorative teeth
        for (i = [0:2]) {
            rotate([0, 0, i*30 - 30])
                translate([0, 5, 12])
                    cube([3, 1, 5], center = true);
        }
    }
}

 // Seat structure
module seat() {
    translate([0, 0, -gondola_height/2 + 15]) {
        // Seat base
        cube([30, gondola_width - 10, wall_thickness], center = true);

        // Seat back
        translate([-10, 0, 5])
            cube([wall_thickness, gondola_width - 10, 10], center = true);

        // Armrests
        translate([0, (gondola_width - 10)/2 - 2, 5])
            cube([25, wall_thickness, 5], center = true);
        translate([0, -(gondola_width - 10)/2 + 2, 5])
            cube([25, wall_thickness, 5], center = true);
    }
}

// Oar support structure
module oar_support() {
    translate([gondola_length/4, 0, -gondola_height/2 + 25]) {
        // Main post
        cylinder(h = 15, r = 3);

        // Fork at top
        translate([0, 0, 7.5])
            union() {
                rotate([0, 90, 0])
                    cylinder(h = 10, r = 2);
                translate([0, 5, 0])
                    rotate([0, 90, 0])
                        cylinder(h = 5, r = 2);
                translate([0, -5, 0])
                    rotate([0, 90, 0])
                        cylinder(h = 5, r = 2);
            }
    }
}

// Decorative stripes on hull
