// Balloon Chair - Realistic style, medium complexity
// A whimsical balloon-shaped chair with internal frame structure

$fn = 32;

// Main parameters
balloon_diameter = 100;
balloon_height = 120;
wall_thickness = 2;
base_thickness = 5;

// Balloon body - main seating area
module balloon_body() {
    difference() {
        // Outer balloon shape
        scale([1, 1, 1.2])
            sphere(d = balloon_diameter);

        // Hollow interior
        scale([1 - (wall_thickness*2/balloon_diameter), 
               1 - (wall_thickness*2/balloon_diameter), 
               1 - (wall_thickness*2/balloon_height)])
            translate([0, 0, wall_thickness/2])
                sphere(d = balloon_diameter - wall_thickness);
    }
}

// Balloon knot detail
module balloon_knot() {
    translate([0, 0, -balloon_height/2 - 8])
        cylinder(h = 10, d1 = 15, d2 = 8, $fn = 16);
}

// Internal support frame
module internal_frame() {
    // Vertical supports
    for(i = [0:120:359]) {
        rotate([0, 0, i])
            translate([balloon_diameter/2 - wall_thickness - 5, 0, -balloon_height/2])
                cylinder(h = balloon_height - 10, d = 4, $fn = 8);
    }

    // Ring supports
    for(h = [-20:30:40]) {
        translate([0, 0, h])
            rotate_extrude(angle = 360, $fn = 64)
                translate([balloon_diameter/2 - wall_thickness - 5, 0, 0])
                    circle(d = 4, $fn = 8);
    }
}

// Seating platform
module seat_platform() {
    translate([0, 0, -balloon_height/2 + 20])
        difference() {
            cylinder(h = 8, d = balloon_diameter - 20, $fn = 64);

            // Ventilation holes
            for(i = [0:45:359]) {
                rotate([0, 0, i])
                    translate([(balloon_diameter - 30)/2, 0, -1])
                        cylinder(h = 10, d = 8, $fn = 16);
            }
        }
}

// Balloon stripes detail
module balloon_stripes() {
    for(i = [0:30:179]) {
        rotate([0, 0, i])
            translate([0, 0, -balloon_height/2])
                scale([1, 0.05, 1.2])
                    sphere(d = balloon_diameter + 0.5);
    }
}

// Base with legs
module chair_base() {
    // Main base ring
    translate([0, 0, -balloon_height/2 - 25])
        difference() {
            cylinder(h = 8, d = balloon_diameter + 20, $fn = 64);
            cylinder(h = 10, d = balloon_diameter + 16, $fn = 64);
        }

    // Support legs
    for(i = [0:90:359]) {
        rotate([0, 0, i])
            translate([(balloon_diameter + 20)/2 - 5, 0, -balloon_height/2 - 25])
                rotate([90, 0, 0])
                    cylinder(h = 25, d = 8, $fn = 8);
    }
}

// Backrest support
module backrest_support() {
    translate([0, -balloon_diameter/3, balloon_height/2 - 10])
        rotate([15, 0, 0])
            scale([1, 0.3, 0.8])
                sphere(d = balloon_diameter - 10);
}

// Assembly
union() {
    // Main balloon structure
    balloon_body();

    // Decorative elements
    balloon_knot();
    balloon_stripes();

    // Furniture elements
    color("lightgray")
        internal_frame();

    color("beige")
        seat_platform();

    color("darkgray")
        chair_base();

    color("lightblue")
        backrest_support();
}

// Rocket Bookshelf / Display Unit
// Medium complexity realistic rocket furniture
// Overall height ~140mm, suitable for desktop use
// Wall thickness 2mm throughout for 3D printing

$fn=50;  // Smooth curves for realistic appearance

// Main module
module rocket_bookshelf() {
    // Rocket body with hollow interior for books
    rocket_body();

    // Fins as structural supports and bookends
    rocket_fins();

    // Nose cone as display shelf
    nose_shelf();

    // Engine section as base storage
    engine_section();

    // Landing legs as furniture feet
    landing_legs();
}

// Main rocket body - hollow cylinder with shelves
module rocket_body() {
    // Outer shell - 2mm thick
    difference() {
        // Outer cylinder
        cylinder(h=100, r=25, center=false);

        // Hollow interior
        translate([0, 0, 2])
        cylinder(h=96, r=23, center=false);

        // Bottom opening
        translate([0, 0, -1])
        cube([50, 50, 2], center=true);
    }

    // Internal shelves (3 levels)
    for(i=[1:3]) {
        translate([0, 0, i*30 - 5])
        difference() {
            cylinder(h=2, r=23, center=false);
            // Cable management holes
            translate([0, 15, 0])
            cylinder(h=3, r=3, center=false);
        }
    }

    // Reinforcement rings
    for(i=[0:3]) {
        translate([0, 0, i*30])
        cylinder(h=3, r=26, center=false);
    }
}

// Fins as bookends and structural support
module rocket_fins() {
    fin_width = 2;
    fin_length = 15;

    for(angle=[0:120:359]) {
        rotate([0, 0, angle])
        translate([0, 25, 50])
        difference() {
            // Main fin structure
            union() {
                // Vertical support
                cube([fin_width, fin_length, 100], center=true);

                // Horizontal brace
                translate([0, 7, 0])
                cube([fin_width, 8, 2], center=true);
            }

            // Lightening holes
            translate([0, 20, 30])
            cylinder(h=3, r=4, center=true);
        }
    }
}

// Nose cone as angled display shelf
module nose_shelf() {
    // Solid cone for structure
    translate([0, 0, 100])
    cylinder(h1=30, h2=0, r1=25, r2=0, center=false);

    // Hollow interior for storage
    translate([0, 0, 102])
    cylinder(h1=26, h2=0, r1=22, r2=0, center=false);

    // Display ring at top
    translate([0, 0, 128])
    difference() {
        cylinder(h=3, r=26, center=false);
        cylinder(h=4, r=22, center=false);
