    // Structural arm with furniture-style joinery
    translate([x, y, 0])
    rotate([0, 0, angle]) {
        // Main arm beam
        translate([arm_length/2, 0, 0])
            cube([arm_length, arm_thickness, arm_thickness], center=true);

        // Decorative joint plate at body connection
        translate([0, 0, 0])
            cylinder(h=wall_thickness*2, r=arm_thickness*1.2, center=true);

        // Motor mount platform at end
        translate([arm_length, 0, arm_thickness/2 + prop_height/2])
            cube([arm_thickness*2, arm_thickness*2, prop_height], center=true);
    }
}

