track_height = 8;
wheel_diameter = 12;
num_wheels = 5;
ground_clearance = 8;

// Main body module
module tank_body() {
    difference() {
        // Main hull
        hull();

        // Driver hatch
        translate([body_length/2 - 15, 0, body_height - 2])
            rotate([0, 20, 0])
            cube([12, 8, 2], center=true);

        // Turret ring hole
        translate([body_length/4, 0, body_height])
            cylinder(h=wall_thickness*2, d=turret_diameter - wall_thickness, $fn=32);
    }
}

// Hull with angled front
module hull() {
    union() {
        // Main rectangular part
        translate([0, 0, body_height/2])
            cube([body_length - 10, body_width, body_height], center=true);

        // Angled front
        translate([body_length/2 - 5, 0, body_height/2])
            rotate([0, 10, 0])
            cube([20, body_width, body_height], center=true);
    }
}

// Turret module
module turret() {
    translate([body_length/4, 0, body_height + turret_height/2]) {
        union() {
            // Main turret body
            cylinder(h=turret_height, d=turret_diameter, $fn=32);

            // Turret top details
            translate([0, 0, turret_height/2])
                cylinder(h=wall_thickness, d=turret_diameter - 10, $fn=32);

            // Commander's cupola
            translate([turret_diameter/4, 0, turret_height/2 + wall_thickness])
                rotate([0, 0, 45])
                cube([12, 12, 8], center=true);

            // Main gun barrel
            translate([turret_diameter/2 + barrel_length/2, 0, 0])
                rotate([0, -5, 0])
                cylinder(h=barrel_length, d=barrel_diameter, $fn=16);

            // Barrel support
            translate([turret_diameter/2 - 5, 0, -turret_height/4])
                cube([10, barrel_diameter + 4, turret_height/2], center=true);
        }
    }
}

// Track module with wheels
module track_assembly(side) {
    y_pos = side * (body_width/2 + track_width/2);

    translate([0, y_pos, ground_clearance + track_height/2]) {
        union() {
            // Track housing
            track_housing();

            // Road wheels
            for(i = [0:num_wheels-1]) {
                x_pos = -body_length/2 + 15 + i * (body_length - 30)/(num_wheels-1);
                translate([x_pos, 0, track_height/2 + wheel_diameter/2])
                    rotate([90, 0, 0])
                    cylinder(h=track_width - wall_thickness, d=wheel_diameter, center=true, $fn=16);
            }

            // Drive sprocket (rear)
            translate([body_length/2 - 15, 0, track_height/2 + wheel_diameter/2])
                rotate([90, 0, 0])
                cylinder(h=track_width - wall_thickness, d=wheel_diameter + 4, center=true, $fn=20);

            // Idler wheel (front)
            translate([-body_length/2 + 15, 0, track_height/2 + wheel_diameter/2])
                rotate([90, 0, 0])
                cylinder(h=track_width - wall_thickness, d=wheel_diameter, center=true, $fn=16);
        }
    }
}

// Track housing
module track_housing() {
    difference() {
        // Outer track
        cube([body_length, track_width, track_height], center=true);

        // Inner cavity
        translate([0, 0, -wall_thickness])
            cube([body_length - wall_thickness*2, track_width - wall_thickness*2, track_height + wall_thickness], center=true);

        // Ground contact pads
        for(i = [0:floor(body_length/8)]) {
            x_pos = -body_length/2 + 4 + i * 8;
            translate([x_pos, 0, -track_height/2 + wall_thickness])
                cube([6, track_width - wall_thickness, wall_thickness], center=true);
        }
    }
}

// Rear storage box
module storage_box() {
    translate([-body_length/2 + 10, 0, body_height + 5]) {
        difference() {
            cube([20, body_width - 20, 8], center=true);
            translate([0, 0, -wall_thickness])
                cube([20 - wall_thickness*2, body_width - 20 - wall_thickness*2, 8 + wall_thickness], center=true);
        }
    }
}

// Exhaust pipe
module exhaust() {
    translate([-body_length/2 + 5, body_width/3, body_height - 5]) {
        rotate([0, 90, 0])
            cylinder(h=15, d=4, $fn=12);
    }
}

// Main assembly
union() {
    // Tank body
    tank_body();

    // Turret
    turret();

    // Left track assembly
    track_assembly(-1);

    // Right track assembly
    track_assembly(1);

    // Rear storage box
    storage_box();

    // Exhaust pipe
    exhaust();
}

