// Main body module
module hatchback_body() {
    // Main cabinet body
    difference() {
        // Outer shell
        cube([width, depth, height]);

        // Hollow interior
        translate([wall, wall, wall])
            cube([width-2*wall, depth-2*wall, height-wall]);
    }

    // Back panel (thicker for stability)
    translate([0, depth-wall, 0])
        cube([width, wall, height]);
}

// Hatchback door module
module hatchback_door() {
    // Door panel
    translate([-wall, 0, 0])
    rotate([0, -30, 0])
    translate([0, 0, 0]) {
        difference() {
            cube([width/2, depth+2*wall, height-wall]);

            // Inner cavity
            translate([wall, wall, wall])
                cube([width/2-2*wall, depth, height-2*wall]);
        }

        // Handle cutout
        translate([width/4, depth/2, height/2])
            rotate([0, 45, 0])
            cube([10, 2, 5]);
