// Miniature 3D-printable escalator furniture piece
// Overall size: 100 mm long Ã— 35 mm wide Ã— 40 mm high
// Wall thickness: 2 mm for 3D-printability

// Basic parameters
$fn=24;
length = 100;
width  = 35;
height = 40;
angle  = 30;          // incline in degrees
wall   = 2;           // wall thickness
step_h = 3;           // step height
step_d = 6;           // step depth
nSteps = 12;          // number of visible steps

// Main module
module escalator() {
    // Left and right side trusses
    for (x=[-1,1])
        translate([x*(width-wall)/2,0,0])
            truss_side();

    // Floor base plate
    translate([0,-5,0])
        cube([length+10,width+10,wall],center=true);

    // Steps on tread belt
    belt_steps();

    // Handrails
    for (x=[-1,1])
        translate([x*(width-wall)/2,0,height])
            handrail();
}

// Side truss frame
module truss_side() {
    difference() {
        // Outer truss skin
        rotate([0,-angle,0])
            translate([0,length/2,0])
                cube([wall,length,height],center=true);

        // Inner cutout
        rotate([0,-angle,0])
            translate([0,length/2,-wall])
                cube([wall+0.1,length-10,height-wall],center=true);
    }
    // Bottom support foot
    translate([0,-length/2+5,wall/2])
        cube([wall,10,wall],center=true);
    // Top support foot
    translate([0,length/2-5,height-wall/2])
        cube([wall,10,wall],center=true);
}

// Tread belt with steps
module belt_steps() {
    for (i=[0:nSteps-1]) {
        // Calculate position along slope
        t = i / (nSteps-1);
        y = t * length - length/2;
        z = t * height * sin(angle);

        translate([0,y,z-wall/2])
            rotate([0,-angle,0])
                single_step();
    }
}

// Single step
module single_step() {
    color("silver")
    translate([0,0,step_h/2])
        cube([width-wall,step_d,step_h],center=true);
}

// Handrail with stanchions
module handrail() {
    // Vertical stanchions
    for (y=[-length/2+5:length/10:length/2-5]) {
        z = (y+length/2)/length * height * sin(angle);
        translate([0,y,z+3])
            cylinder(r=1,h=6);
    }

    // Top rail bar
    hull() {
        translate([0,-length/2+5,3])
            sphere(r=1.5);
        translate([0,length/2-5,height*sin(angle)+3])
            sphere(r=1.5);
    }
}

// Render the model
escalator();

