        difference() {
            cylinder(h=wheel_w, d=wheel_d, center=true);
            cylinder(h=wheel_w+1, d=wheel_d-frame_t*2, center=true);
        }

        // Wheel rim
        rotate([90, 0, 0])
        difference() {
            cylinder(h=wheel_w, d=wheel_d-frame_t*2, center=true);
            cylinder(h=wheel_w+1, d=wheel_d-frame_t*4, center=true);
        }

        // Spokes
        for(i=[0:30:330]) {
            rotate([90, 0, i])
            cube([wheel_d/2-frame_t*3, frame_t, frame_t], center=true);
        }

        // Hub
        rotate([90, 0, 0])
        cylinder(h=wheel_w+4, d=10, center=true);
    }

    // Frame structure
    union() {
        // Main fork legs
        translate([0, 0, wheel_d/2+leg_h/2])
        rotate([0, 90, 0])
        cylinder(h=leg_h, d=frame_t*3);

        translate([0, wheel_w/2, wheel_d/2+leg_h/2])
        rotate([0, 90, 0])
        cylinder(h=leg_h, d=frame_t*3);

